// Archivo para crear todas las tablas necesarias en la base de datos durante el despliegue en Railway
import pg from 'pg';
const { Pool } = pg;
import { drizzle } from 'drizzle-orm/node-postgres';
import { eq } from 'drizzle-orm';
import * as schema from './shared/schema.ts';
import bcrypt from 'bcrypt';

const { users, integrations, pricingPlans, welcomeMessages } = schema;

async function setupDatabase() {
  console.log('üèóÔ∏è  Configurando base de datos inicial para Railway...');

  if (!process.env.DATABASE_URL) {
    console.error('‚ùå ERROR: DATABASE_URL no est√° configurado. No se puede inicializar la base de datos.');
    process.exit(1);
  }

  try {
    console.log('üîå Conectando a la base de datos Railway PostgreSQL...');
    const pool = new Pool({ 
      connectionString: process.env.DATABASE_URL,
      ssl: process.env.NODE_ENV === 'production' ? { rejectUnauthorized: false } : false
    });
    const db = drizzle(pool, { schema });

    console.log('‚úÖ Conexi√≥n a base de datos establecida');

    // Crear usuario administrador por defecto si no existe
    const adminEmail = 'admin@aipi.com';
    try {
      const existingAdmin = await db.select().from(users).where(eq(users.email, adminEmail)).limit(1);

      if (existingAdmin.length === 0) {
        console.log('üë§ Creando usuario administrador por defecto...');

        const hashedPassword = await bcrypt.hash('admin123', 10);
        const apiKey = 'aipi_admin_' + Math.random().toString(36).substring(2, 15);

        await db.insert(users).values({
          username: 'admin',
          email: adminEmail,
          password: hashedPassword,
          fullName: 'Administrador AIPI',
          apiKey: apiKey
        });

        console.log('‚úÖ Usuario administrador creado');
      } else {
        console.log('üë§ Usuario administrador ya existe');
      }

      // Crear usuario Pablo si no existe
      const pabloEmail = 'techcolca@gmail.com';
      const existingPablo = await db.select().from(users).where(eq(users.email, pabloEmail)).limit(1);

      if (existingPablo.length === 0) {
        console.log('üë§ Creando usuario Pablo...');

        const hashedPasswordPablo = await bcrypt.hash('pablo123', 10);
        const apiKeyPablo = 'aipi_pablo_' + Math.random().toString(36).substring(2, 15);

        const [pablo] = await db.insert(users).values({
          username: 'Pablo',
          email: pabloEmail,
          password: hashedPasswordPablo,
          fullName: 'Pablo Techcolca',
          apiKey: apiKeyPablo
        }).returning();

        console.log('‚úÖ Usuario Pablo creado con ID:', pablo.id);

        // Crear integraci√≥n b√°sica para Pablo
        console.log('üîó Creando integraci√≥n para Pablo...');
        await db.insert(integrations).values({
          userId: pablo.id,
          name: 'Sitio Principal',
          url: 'https://mi-sitio.com',
          apiKey: 'int_pablo_' + Math.random().toString(36).substring(2, 15),
          themeColor: '#3b82f6',
          position: 'bottom-right',
          active: true,
          widgetType: 'bubble',
          language: 'es',
          description: 'Integraci√≥n principal de Pablo',
          botBehavior: 'S√© amable y profesional, responde de manera precisa a las preguntas sobre el sitio web.'
        });
        console.log('‚úÖ Integraci√≥n de Pablo creada');

      } else {
        console.log('üë§ Usuario Pablo ya existe');
      }

    } catch (error) {
      console.log('‚ö†Ô∏è  No se pudo crear usuarios (tabla puede no existir a√∫n):', error.message);
    }

    // Crear planes de precios por defecto si no existen
    console.log('üí∞ Verificando planes de precios...');
    try {
      const existingPlans = await db.select().from(pricingPlans).limit(1);

      if (existingPlans.length === 0) {
