  // Helper function to get font family based on config
  function getFontFamily() {
    switch (config.font) {
      case 'roboto':
        return "'Roboto', sans-serif";
      case 'opensans':
        return "'Open Sans', sans-serif";
      case 'lato':
        return "'Lato', sans-serif";
