  // Helper function to send fullscreen messages using bubble system
  async function sendFullscreenMessage(message) {
    showTypingIndicator(true);

    try {
      console.log('AIPPS Debug: Enviando mensaje fullscreen autenticado...');
      console.log('AIPPS Debug: Mensaje:', message);
      console.log('AIPPS Debug: Current conversation ID:', currentConversationId);

      const token = getAuthToken();

      if (!token) {
        console.log('AIPPS Debug: No auth token found, user needs to login');
        addMessage('Sesi√≥n expirada. Por favor, inicia sesi√≥n nuevamente.', 'assistant');
        showTypingIndicator(false);
        return;
      }

      // If no current conversation, create a new one first
      if (!currentConversationId) {
        console.log('AIPPS Debug: No hay conversaci√≥n actual, creando nueva autenticada...');
        const newConversation = await createNewConversationForDashboard();
        if (newConversation) {
          currentConversationId = newConversation.id;
          console.log('AIPPS Debug: Nueva conversaci√≥n autenticada creada con ID:', currentConversationId);
        } else {
          console.error('AIPPS Debug: Error al crear nueva conversaci√≥n autenticada');
          addMessage('Error al crear nueva conversaci√≥n. Por favor, inicia sesi√≥n nuevamente.', 'assistant');
          showTypingIndicator(false);
          return;
        }
      }

      // Send message to existing conversation using authenticated endpoint
      const response = await fetch(`${getApiBaseUrl()}/api/widget/${config.apiKey}/conversation/${currentConversationId}/send`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          message: message,
          currentUrl: window.location.href,
          pageTitle: document.title
        })
      });

      console.log('AIPPS Debug: Send authenticated message response status:', response.status);

      if (response.ok) {
        const data = await response.json();
        console.log('AIPPS Debug: Authenticated message response data:', data);

        addMessage(data.response || data.message || 'Respuesta recibida', 'assistant');

        // Update conversations list to reflect changes
        await loadUserConversationsFromDashboard();
        updateConversationsList();
      } else if (response.status === 401) {
        console.log('AIPPS Debug: Token expired during message sending');
        // Clear invalid token
        localStorage.removeItem('aipi_auth_token');
        if (typeof document !== 'undefined' && document.cookie) {
          document.cookie = 'auth_token=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;';
        }
        addMessage('Sesi√≥n expirada. Por favor, inicia sesi√≥n nuevamente.', 'assistant');
      } else {
        const errorData = await response.text();
        console.error('Error response:', response.status, errorData);
        addMessage('Lo siento, no pude procesar tu mensaje. Int√©ntalo de nuevo.', 'assistant');
      }
    } catch (error) {
      console.error('Error sending authenticated message:', error);
      addMessage('Error de conexi√≥n. Por favor, verifica tu conexi√≥n a internet.', 'assistant');
    } finally {
      showTypingIndicator(false);
    }
  }

  // Function to update text colors after config is loaded
  function updateTextColorsAfterConfig() {
    console.log('üîß AIPPS: Recalculando colores despu√©s de cargar configuraci√≥n');
    console.log('  Color de fondo del asistente:', config.assistantBubbleColor);
    console.log('  Modo de color de texto:', config.textColorMode);

    // Calculate text color using new system
    const bgColor = config.assistantBubbleColor || '#E5E7EB';
    const textColor = getContrastTextColor(bgColor);

    console.log('üé® AIPPS: Rec√°lculo de colores despu√©s de config:');
    console.log('  Color de fondo:', bgColor);
    console.log('  Modo configurado:', config.textColorMode);
    console.log('  Color de texto final:', textColor);

    // Update CSS variables or create new styles
    const existingStyles = document.querySelector('#aipi-dynamic-colors');
    if (existingStyles) {
      existingStyles.remove();
    }

    const dynamicStyles = document.createElement('style');
    dynamicStyles.id = 'aipi-dynamic-colors';
    dynamicStyles.textContent = `
      .aipi-assistant-message {
        background-color: ${bgColor} !important;
        color: ${textColor} !important;
      }

      .aipi-assistant-message * {
        color: ${textColor} !important;
      }

      .aipi-typing-indicator {
        background-color: ${bgColor} !important;
        color: ${textColor} !important;
      }

      .aipi-typing-indicator * {
        color: ${textColor} !important;
      }
    `;

    document.head.appendChild(dynamicStyles);
    console.log('‚úÖ AIPPS: Estilos din√°micos aplicados con color:', textColor);
  }

  // Expose other functions to global scope
  window.aipiSendMessage = sendMessage;
  // Function to update existing welcome messages with correct assistant name
  function updateExistingWelcomeMessages() {
    console.log('AIPPS Debug: Actualizando mensajes de bienvenida existentes');

    // Find existing assistant messages that might be welcome messages
    const assistantMessages = document.querySelectorAll('.aipi-assistant-message');
    const assistantName = config.assistantName || config.integrationName || 'tu asistente';

    assistantMessages.forEach((msg, index) => {
      if (index === 0) { // Usually the first message is the welcome message
        const text = msg.textContent || msg.innerText;

        // Check if this looks like a welcome message with "AIPPS"
        if (text.includes('AIPPS') || text.includes('Soy AIPPS')) {
          const newMessage = text.replace(/AIPPS/g, assistantName).replace(/Soy AIPPS/g, `Soy ${assistantName}`);

          if (msg.innerHTML !== newMessage) {
            console.log('AIPPS Debug: Actualizando mensaje de bienvenida:', text, '‚Üí', newMessage);
            msg.innerHTML = formatAssistantMessage(newMessage);
          }
        }
      }
    });
  }

  window.aipiCloseWidget = closeWidget;
  window.aipiOpenWidget = openWidget;
  window.aipiMinimizeWidget = minimizeWidget;
  window.aipiMaximizeWidget = maximizeWidget;
  window.aipiUpdateTextColors = updateTextColorsAfterConfig; // Para debug

  // Initialize the widget when the DOM is fully loaded
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', init);
  } else {
    init();
  }
})();

