// Funci√≥n para detectar el idioma del mensaje del usuario
function detectLanguage(message: string): string {

  const text = message.toLowerCase().trim();

  // Palabras comunes en espa√±ol
  const spanishWords = [
    "hola", "gracias", "por favor", "adi√≥s", "s√≠", "no", "c√≥mo", "qu√©", "d√≥nde", "cu√°ndo",
    "qui√©n", "por qu√©", "ayuda", "informaci√≥n", "precio", "servicio", "empresa", "contacto",
    "productos", "disponible", "horario", "ubicaci√≥n", "tel√©fono", "correo", "p√°gina",
    "necesito", "quiero", "busco", "me interesa", "puedo", "tienes", "tienen", "ofrecen",
    "buenos d√≠as", "buenas tardes", "buenas noches", "muchas gracias", "de nada", "est√°",
    "son", "estoy", "tengo", "puede", "hacer", "muy", "bien", "malo", "bueno"
  ];

  // Palabras comunes en franc√©s
  const frenchWords = [
    "bonjour", "merci", "au revoir", "oui", "non", "comment", "quoi", "o√π", "quand",
    "qui", "pourquoi", "aide", "information", "prix", "service", "entreprise", "contact",
    "produits", "disponible", "horaire", "emplacement", "t√©l√©phone", "email", "page",
    "besoin", "veux", "cherche", "int√©resse", "puis", "avez", "offrez", "vous",
    "journ√©e", "soir√©e", "nuit", "beaucoup", "rien", "salut", "est", "sont", "suis",
    "avoir", "√™tre", "faire", "tr√®s", "bien", "mal", "bon", "bonne"
  ];

  // Palabras comunes en ingl√©s
  const englishWords = [
    "hello", "thank", "please", "goodbye", "yes", "no", "how", "what", "where", "when",
    "who", "why", "help", "information", "price", "service", "company", "contact",
    "products", "available", "schedule", "location", "phone", "email", "page",
    "need", "want", "looking", "interested", "can", "have", "offer", "you",
    "morning", "afternoon", "evening", "night", "much", "welcome", "hi", "are",
    "is", "am", "do", "make", "very", "good", "bad", "well"
  ];

  let spanishScore = 0;
  let frenchScore = 0;
  let englishScore = 0;

  // Contar coincidencias para cada idioma
  spanishWords.forEach(word => {
    if (text.includes(word)) {
      spanishScore++;
    }
  });

  frenchWords.forEach(word => {
    if (text.includes(word)) {
      frenchScore++;
    }
  });

  englishWords.forEach(word => {
    if (text.includes(word)) {
      englishScore++;
    }
  });

  // Determinar idioma con mayor puntuaci√≥n
  if (spanishScore > frenchScore && spanishScore > englishScore) {
    return "es";
  } else if (frenchScore > spanishScore && frenchScore > englishScore) {
    return "fr";
  } else if (englishScore > 0) {
    return "en";
  } else {
    return "es";
  }
}

// Funci√≥n para extraer contenido de documentos
async function extractDocumentContent(doc: any): Promise<string> {
  let content = `Informaci√≥n del archivo: ${doc.originalName || doc.filename}`;

  if (doc.path && fs.existsSync(doc.path)) {
    try {
      if (doc.mimetype === "application/vnd.openxmlformats-officedocument.wordprocessingml.document") {
        try {
          const mammoth = await import("mammoth");
          const fileBuffer = fs.readFileSync(doc.path);
          const result = await mammoth.extractRawText({ buffer: fileBuffer });
          content = `Documento Word: ${doc.originalName}\n\nContenido:\n${result.value}`;
        } catch (mammothError) {
          console.error(`Error extracting DOCX content from ${doc.originalName}:`, mammothError);
          content = `Documento Word: ${doc.originalName}. Error al extraer contenido autom√°ticamente.`;
        }
      } else if (doc.mimetype === "application/pdf") {
        try {
          const pdfParse = await import("pdf-parse");
          const fileBuffer = fs.readFileSync(doc.path);
          const pdfData = await pdfParse.default(fileBuffer);
          content = `Documento PDF: ${doc.originalName}\n\nContenido:\n${pdfData.text}`;
        } catch (pdfError) {
          console.error(`Error extracting PDF content from ${doc.originalName}:`, pdfError);
          content = `Documento PDF: ${doc.originalName}. Error al extraer contenido autom√°ticamente.`;
        }
      } else if (doc.mimetype === "text/plain") {
        content = fs.readFileSync(doc.path, "utf8");
      } else {
        content = `Archivo ${doc.originalName}: Contiene informaci√≥n relevante sobre su organizaci√≥n.`;
      }
    } catch (error) {
      content = `Documento ${doc.originalName}: Informaci√≥n no disponible para procesamiento autom√°tico.`;
    }
  }

  return content;
}
