      }

      if (!discountCode.isActive) {
        return res.status(400).json({ message: "Este c√≥digo de descuento ya no est√° activo" });
      }

      // Verificar si el c√≥digo ha expirado
      if (discountCode.expiresAt && new Date(discountCode.expiresAt) < new Date()) {
        return res.status(400).json({ message: "Este c√≥digo de descuento ha expirado" });
      }

      // Verificar si se ha alcanzado el l√≠mite de uso
      if (discountCode.usageLimit && discountCode.usageCount >= discountCode.usageLimit) {
        return res.status(400).json({ message: "Este c√≥digo de descuento ha alcanzado su l√≠mite de uso" });
      }

      // Verificar si es aplicable al nivel solicitado
      if (discountCode.applicableTier !== 'all' && discountCode.applicableTier !== tier) {
        return res.status(400).json({ 
          message: `Este c√≥digo de descuento solo es v√°lido para el nivel ${discountCode.applicableTier}` 
        });
      }

      // Todo correcto, devolver informaci√≥n del descuento
      res.json({
        valid: true,
        code: discountCode.code,
        discountPercentage: discountCode.discountPercentage,
        name: discountCode.name
      });
    } catch (error) {
      console.error("Error al validar c√≥digo de descuento:", error);
      res.status(500).json({ message: "Error del servidor" });
    }
  });

