    generateDiscountCode(prefix: string = 'AIPPS'): string {
      // Generar un c√≥digo alfanum√©rico √∫nico de 8 caracteres
      const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
      let result = prefix;
      const charactersLength = characters.length;

      // Asegurar que el prefijo no sea m√°s de 4 caracteres
      if (result.length > 4) {
        result = result.substring(0, 4);
      }

      // A√±adir gui√≥n despu√©s del prefijo
      result += '-';

      // A√±adir 4 caracteres aleatorios
      for (let i = 0; i < 4; i++) {
        result += characters.charAt(Math.floor(Math.random() * charactersLength));
      }

      return result;
    }

