/*
 * AIPI Fullscreen Chat Widget (ChatGPT Style)
 * 
 * Versi√≥n que emula la interfaz de ChatGPT para una experiencia m√°s familiar.
 * Este script crea un bot√≥n flotante que al hacer clic abre un chat en pantalla completa.
 */
(function() {
  // Funci√≥n para obtener traducciones seg√∫n el idioma configurado en la integraci√≥n
  function getTranslations(language = null) {
    // Usar el idioma de la integraci√≥n, luego el del navegador como fallback
    const lang = language || config.language || navigator.language.substring(0, 2);
    const translations = {
      es: {
        placeholder: "Escribe tu mensaje...",
        newConversation: "Nueva conversaci√≥n"
      },
      en: {
        placeholder: "Type your message...",
        newConversation: "New conversation"
      },
      fr: {
        placeholder: "Tapez votre message...",
        newConversation: "Nouvelle conversation"
      }
    };
    return translations[lang] || translations.en;
  }

  let t = getTranslations(); // Se actualizar√° despu√©s de cargar la configuraci√≥n

  // Configuraci√≥n inicial y datos de estado
  let config = {
    apiKey: '',
    serverUrl: window.location.origin,
    mainColor: '#19c37d', // Color verde t√≠pico de ChatGPT
    title: 'AIPI Assistant',
    greetingMessage: '¬°Hola! Soy un asistente virtual. ¬øEn qu√© puedo ayudarte hoy?',
    position: 'bottom-right'
  };

  let conversationId = null;
  let visitorId = localStorage.getItem('aipi_visitor_id') || 
    'visitor_' + Math.random().toString(36).substring(2, 15);

  // Guardar ID de visitante
  localStorage.setItem('aipi_visitor_id', visitorId);

  // Inicializar widget
  document.addEventListener('DOMContentLoaded', initialize);

  // Detectar si hay formularios AIPPS activos en la p√°gina
  function detectActiveAippsForm() {
    // Verificar atributo marcador del formulario
    if (document.documentElement.hasAttribute('data-aipps-form-active')) {
      console.log('AIPI Widget: Formulario AIPPS activo detectado por atributo');
      return true;
    }

    // Buscar contenedores de formularios AIPPS
    const formContainers = document.querySelectorAll('[id*="aipps-form"], [class*="aipps-form"], [data-aipps-form]');
    if (formContainers.length > 0) {
      console.log('AIPI Widget: Contenedor de formulario detectado');
      return true;
    }

    // Buscar scripts de formulario activos
    const formScripts = document.querySelectorAll('script[src*="form-embed.js"]');
    if (formScripts.length > 0) {
      console.log('AIPI Widget: Script de formulario detectado');
      return true;
    }

    // Buscar elementos del wrapper del formulario moderno
    const modernFormWrappers = document.querySelectorAll('.aipi-modern-form-wrapper');
    if (modernFormWrappers.length > 0) {
      console.log('AIPI Widget: Wrapper de formulario moderno detectado');
      return true;
    }

    return false;
  }

  // Funci√≥n principal de inicializaci√≥n
  function initialize() {
    console.log('AIPI Widget: Inicializando...');

    // Verificar si hay formularios activos antes de continuar
    if (detectActiveAippsForm()) {
      console.log('AIPI Widget: No se iniciar√° el widget porque hay un formulario activo');
      return;
    }

    try {
      // Cargar configuraci√≥n
      loadConfig();

      // Crear elementos del DOM
      createWidgetElements();

      // Adjuntar eventos
      attachEvents();

      console.log('AIPI Widget: Inicializado correctamente');
    } catch (error) {
      console.error('AIPI Widget Error:', error);
      alert('Error al inicializar el widget AIPI: ' + error.message);
    }

    // Verificaci√≥n peri√≥dica para formularios que se cargan din√°micamente
    setInterval(() => {
      if (detectActiveAippsForm()) {
        const existingWidget = document.querySelector('[id*="aipi"], [id*="chat-widget"]');
        if (existingWidget && existingWidget.style.display !== 'none') {
          console.log('AIPI Widget: Verificaci√≥n peri√≥dica - ocultando por formulario activo');
          existingWidget.style.display = 'none';
        }
      }
    }, 3000);
  }

  // Cargar configuraci√≥n desde el script
  async function loadConfig() {
    try {
      // Obtener elemento de script
      const scripts = document.getElementsByTagName('script');
      let scriptElement = null;

      // Buscar el script correcto que contiene la clave API
      for (let i = 0; i < scripts.length; i++) {
        const src = scripts[i].src || '';
        if (src.includes('chatgpt-embed.js')) {
          scriptElement = scripts[i];
          break;
        }
      }

      if (!scriptElement) {
        throw new Error('No se pudo encontrar el script del widget');
      }

      // Obtener clave API del src o atributo data
      const scriptSrc = scriptElement.src;
      const urlParams = new URLSearchParams(scriptSrc.split('?')[1] || '');
      config.apiKey = urlParams.get('key') || scriptElement.getAttribute('data-api-key');

      if (!config.apiKey) {
        throw new Error('Se requiere una clave API');
      }

      console.log('AIPI Widget: Clave API cargada - ' + config.apiKey);

      // Obtener URL del servidor si se proporciona
      const serverUrl = scriptElement.getAttribute('data-server-url');
      if (serverUrl) {
        config.serverUrl = serverUrl;
        console.log('AIPI Widget: URL del servidor personalizada - ' + config.serverUrl);
      } else {
        // Extraer el dominio del script como URL del servidor
        try {
          const scriptUrl = new URL(scriptSrc);
          config.serverUrl = scriptUrl.origin;
          console.log('AIPI Widget: URL del servidor desde script - ' + config.serverUrl);
        } catch (error) {
          console.log('AIPI Widget: Error al extraer dominio, usando predeterminado');
        }
      }

      // Obtener color del tema
      const themeColor = scriptElement.getAttribute('data-theme-color');
      if (themeColor) config.mainColor = themeColor;

      // Obtener posici√≥n
      const position = scriptElement.getAttribute('data-position');
      if (position) config.position = position;

      // Obtener t√≠tulo
      const title = scriptElement.getAttribute('data-title');
      if (title) config.title = title;

      // Obtener mensaje de saludo
      const greeting = scriptElement.getAttribute('data-greeting');
      if (greeting) config.greetingMessage = greeting;

      // Cargar datos de integraci√≥n desde el servidor
      const response = await fetch(`${config.serverUrl}/api/widget/${config.apiKey}`);
      if (!response.ok) {
        throw new Error(`Error al cargar datos de integraci√≥n: ${response.status}`);
      }

      const data = await response.json();

      // Sobrescribir configuraci√≥n con datos del servidor
      if (data.integration) {
        if (data.integration.themeColor) {
          config.mainColor = data.integration.themeColor;
        }

        if (data.integration.position) {
          config.position = data.integration.position;
        }

        if (data.integration.language) {
          config.language = data.integration.language;
        }

        // El botBehavior no debe usarse como mensaje de bienvenida
        // Solo usamos defaultGreeting o welcomeMessage para mostrar al usuario
      }

      // Actualizar traducciones con el idioma de la integraci√≥n
      t = getTranslations(config.language);

      console.log('AIPI Widget: Configuraci√≥n cargada correctamente');
    } catch (error) {
      console.error('AIPI Widget Error:', error);
      throw new Error('Error al cargar la configuraci√≥n: ' + error.message);
    }
  }

  // Crear elementos del widget
  function createWidgetElements() {
    try {
      // Crear estilos
      const styleEl = document.createElement('style');
      styleEl.textContent = getStylesCSS();
      document.head.appendChild(styleEl);

      // Crear contenedor del bot√≥n
      const buttonContainer = document.createElement('div');
      buttonContainer.id = 'aipi-chat-button-container';

      // Calcular posici√≥n del bot√≥n
      const positionStyle = getPositionStyle(config.position);
      buttonContainer.style.position = 'fixed';
      buttonContainer.style.zIndex = '9998';
      buttonContainer.style.bottom = positionStyle.bottom;
      buttonContainer.style.right = positionStyle.right;
      buttonContainer.style.left = positionStyle.left;
      buttonContainer.style.top = positionStyle.top;

      // Crear bot√≥n
      const button = document.createElement('button');
      button.id = 'aipi-chat-button';
      button.style.backgroundColor = config.mainColor;
      button.innerHTML = `
        <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
          <path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2z"></path>
        </svg>
        <span>Chat</span>
      `;

      buttonContainer.appendChild(button);
      document.body.appendChild(buttonContainer);

      // Crear panel de chat con barra lateral
      const chatPanel = document.createElement('div');
      chatPanel.id = 'aipi-chat-panel';
      chatPanel.innerHTML = `
        <div id="aipi-chat-header">
          <div id="aipi-chat-header-title">
            <div id="aipi-chat-avatar" style="background-color: ${config.mainColor};">
              <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
                <path d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z"></path>
              </svg>
            </div>
            ${config.title}
          </div>
          <button id="aipi-chat-close">√ó</button>
        </div>

        <div id="aipi-chat-body">
          <!-- Barra lateral con sugerencias -->
          <div id="aipi-chat-sidebar">
            <div id="aipi-sidebar-header">
              <h3>Conversaciones sugeridas</h3>
            </div>
            <div id="aipi-suggested-topics">
              <!-- Estas sugerencias se generar√°n din√°micamente -->
              <div class="aipi-topic" data-query="¬øQu√© servicios ofrece AIPI?">
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><circle cx="11" cy="11" r="8"></circle><line x1="21" y1="21" x2="16.65" y2="16.65"></line></svg>
                <span>¬øQu√© servicios ofrece AIPI?</span>
              </div>
              <div class="aipi-topic" data-query="¬øC√≥mo puedo integrar el chatbot en mi sitio web?">
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><circle cx="11" cy="11" r="8"></circle><line x1="21" y1="21" x2="16.65" y2="16.65"></line></svg>
                <span>¬øC√≥mo puedo integrar el chatbot?</span>
              </div>
              <div class="aipi-topic" data-query="Explica c√≥mo funciona el an√°lisis de contenido">
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><circle cx="11" cy="11" r="8"></circle><line x1="21" y1="21" x2="16.65" y2="16.65"></line></svg>
                <span>An√°lisis de contenido</span>
              </div>
              <div class="aipi-topic" data-query="¬øCu√°les son las ventajas de usar inteligencia artificial?">
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><circle cx="11" cy="11" r="8"></circle><line x1="21" y1="21" x2="16.65" y2="16.65"></line></svg>
                <span>Ventajas de la IA</span>
              </div>
              <div class="aipi-topic" data-query="¬øQu√© tipos de documentos puedo subir para entrenar al chatbot?">
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><circle cx="11" cy="11" r="8"></circle><line x1="21" y1="21" x2="16.65" y2="16.65"></line></svg>
                <span>Documentos compatibles</span>
              </div>
            </div>
            <div id="aipi-new-chat">
              <button id="aipi-new-chat-button">
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><line x1="12" y1="5" x2="12" y2="19"></line><line x1="5" y1="12" x2="19" y2="12"></line></svg>
${t.newConversation}
              </button>
            </div>
          </div>

          <!-- Panel principal de chat -->
          <div id="aipi-chat-main">
            <div id="aipi-chat-messages">
              <!-- Los mensajes se a√±adir√°n aqu√≠ -->
            </div>
            <div id="aipi-chat-input-area">
              <textarea id="aipi-chat-input" placeholder="${t.placeholder}" rows="1"></textarea>
              <button id="aipi-chat-send" disabled style="background-color: ${config.mainColor};">
                <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
                  <line x1="22" y1="2" x2="11" y2="13"></line>
                  <polygon points="22 2 15 22 11 13 2 9 22 2"></polygon>
                </svg>
              </button>
            </div>
          </div>
        </div>
      `;

      document.body.appendChild(chatPanel);

      console.log('AIPI Widget: Elementos creados correctamente');
    } catch (error) {
      console.error('AIPI Widget Error:', error);
      throw new Error('Error al crear los elementos del widget: ' + error.message);
    }
  }

  // Adjuntar eventos a los elementos
  function attachEvents() {
    try {
      // Evento del bot√≥n para abrir el chat
      const chatButton = document.getElementById('aipi-chat-button');
      if (chatButton) {
        chatButton.onclick = openChat;
        console.log('AIPI Widget: Evento de clic adjuntado al bot√≥n de chat');
      }

      // Evento del bot√≥n para cerrar el chat
      const closeButton = document.getElementById('aipi-chat-close');
      if (closeButton) {
        closeButton.onclick = closeChat;
      }

      // Eventos del campo de entrada
      const inputField = document.getElementById('aipi-chat-input');
      const sendButton = document.getElementById('aipi-chat-send');

      if (inputField && sendButton) {
        // Auto-expandir textarea
        inputField.addEventListener('input', function() {
          // Restablecer altura
          this.style.height = 'auto';
          // Establecer nueva altura seg√∫n el contenido
          this.style.height = (this.scrollHeight) + 'px';
          // Habilitar/deshabilitar bot√≥n seg√∫n contenido
          sendButton.disabled = !this.value.trim();
        });

        // Enviar mensaje al presionar Enter (sin Shift)
        inputField.addEventListener('keydown', function(e) {
          if (e.key === 'Enter' && !e.shiftKey && this.value.trim()) {
            e.preventDefault(); // Evitar salto de l√≠nea
            sendMessage();
          }
        });

        // Enviar mensaje al hacer clic en el bot√≥n
        sendButton.onclick = sendMessage;
      }

      // Eventos para los temas sugeridos
      const topicElements = document.querySelectorAll('.aipi-topic');
      topicElements.forEach(topic => {
        topic.addEventListener('click', function() {
          const query = this.getAttribute('data-query');
          if (query) {
            const inputField = document.getElementById('aipi-chat-input');
            if (inputField) {
              inputField.value = query;
              // Disparar el evento input para activar el bot√≥n
              const event = new Event('input', { bubbles: true });
              inputField.dispatchEvent(event);
              // Enfocar el campo
              inputField.focus();
            }
          }
        });
      });

      // Evento para nueva conversaci√≥n
      const newChatButton = document.getElementById('aipi-new-chat-button');
      if (newChatButton) {
        newChatButton.addEventListener('click', function() {
          // Limpiar mensajes existentes
          const messagesContainer = document.getElementById('aipi-chat-messages');
          if (messagesContainer) {
            messagesContainer.innerHTML = '';
          }

          // Reiniciar conversaci√≥n
          conversationId = null;
          startConversation();
        });
      }

      console.log('AIPI Widget: Todos los eventos adjuntados correctamente');
    } catch (error) {
      console.error('AIPI Widget Error:', error);
      alert('Error al configurar los eventos del widget: ' + error.message);
    }
  }

  // Abrir el chat
  function openChat() {
    console.log('AIPI Widget: Abriendo chat...');
    try {
      // Ocultar bot√≥n
      const buttonContainer = document.getElementById('aipi-chat-button-container');
      if (buttonContainer) {
        buttonContainer.style.display = 'none';
      }

      // Mostrar panel de chat
      const chatPanel = document.getElementById('aipi-chat-panel');
      if (chatPanel) {
        chatPanel.style.display = 'flex';
      }

      // Iniciar conversaci√≥n si es necesario
      if (!conversationId) {
        startConversation();
      }

      // Enfocar campo de entrada
      setTimeout(function() {
        const input = document.getElementById('aipi-chat-input');
        if (input) input.focus();
      }, 300);

      console.log('AIPI Widget: Chat abierto');
    } catch (error) {
      console.error('AIPI Widget Error:', error);
      alert('Error al abrir el chat: ' + error.message);
    }
  }

  // Cerrar el chat
  function closeChat() {
    console.log('AIPI Widget: Cerrando chat...');
    try {
      // Ocultar panel de chat
      const chatPanel = document.getElementById('aipi-chat-panel');
      if (chatPanel) {
        chatPanel.style.display = 'none';
      }

