    }
  }

  // Iniciar conversaci√≥n
  async function startConversation() {
    console.log('AIPI Widget: Iniciando conversaci√≥n...');
    try {
      // Extraer contenido de la p√°gina
      const pageTitle = document.title;
      const pageContent = document.body.innerText.substring(0, 10000); // Limitar a 10k caracteres

      // Crear conversaci√≥n en el servidor
      console.log('AIPI Widget: Creando conversaci√≥n en el servidor...');
      const response = await fetch(`${config.serverUrl}/api/widget/${config.apiKey}/conversation`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          visitorId: visitorId,
          pageContext: {
            title: pageTitle,
            url: window.location.href,
            content: pageContent
          }
        })
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error('AIPI Widget Error al crear conversaci√≥n:', errorText);
        throw new Error(`Error al crear conversaci√≥n: ${response.status}`);
      }

      const data = await response.json();
      console.log('AIPI Widget: Conversaci√≥n creada', data);
      conversationId = data.id;

      // A√±adir mensaje de saludo
      addMessage(config.greetingMessage, 'assistant');

      console.log('AIPI Widget: Conversaci√≥n iniciada con ID', conversationId);
    } catch (error) {
      console.error('AIPI Widget Error:', error);
      addMessage(`Lo siento, hubo un problema al iniciar la conversaci√≥n: ${error.message}. Comprueba que la URL del servidor sea correcta. Puedes a√±adir el atributo 'data-server-url' al script con la URL completa de tu servidor.`, 'assistant');
    }
  }

  // Enviar mensaje
  async function sendMessage() {
    const inputField = document.getElementById('aipi-chat-input');
    const message = inputField.value.trim();

    if (!message) return;

    // Limpiar campo de entrada
    inputField.value = '';
    inputField.style.height = 'auto'; // Restablecer altura del textarea
    document.getElementById('aipi-chat-send').disabled = true;

    // A√±adir mensaje del usuario a la interfaz
    addMessage(message, 'user');

    // Mostrar indicador de escritura
    showTypingIndicator(true);

    try {
      // Iniciar conversaci√≥n si es necesario
      if (!conversationId) {
        await startConversation();
      }

      // Enviar mensaje al servidor
      console.log('AIPI Widget: Enviando mensaje...', { conversationId, message });
      const response = await fetch(`${config.serverUrl}/api/widget/${config.apiKey}/message`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          conversationId: conversationId,
          content: message,
          role: 'user'
        })
      });

      // Ocultar indicador de escritura
      showTypingIndicator(false);

      if (!response.ok) {
        const errorText = await response.text();
        console.error('AIPI Widget Error:', errorText);
        if (response.status === 500) {
          addMessage('Lo siento, hay un problema temporal con el servicio. Por favor, intenta de nuevo m√°s tarde.', 'assistant');
        } else {
          addMessage(`Lo siento, no pude procesar tu mensaje (Error ${response.status}). Verifica que la URL del servidor sea correcta usando el atributo 'data-server-url' en el script.`, 'assistant');
        }
        throw new Error(`Error al enviar mensaje: ${response.status}`);
      }

      const data = await response.json();
      console.log('AIPI Widget: Respuesta recibida', data);

      // A√±adir respuesta de la IA
      if (data.aiMessage && data.aiMessage.content) {
        addMessage(data.aiMessage.content, 'assistant');
      } else {
        addMessage('Recib√≠ tu mensaje, pero no pude generar una respuesta en este momento.', 'assistant');
      }
    } catch (error) {
      console.error('AIPI Widget Error:', error);

      // Asegurar que el indicador de escritura se oculta
      showTypingIndicator(false);
    }
  }

  // A√±adir mensaje a la interfaz
  function addMessage(content, role) {
    const messagesContainer = document.getElementById('aipi-chat-messages');
    if (!messagesContainer) return;

    const messageEl = document.createElement('div');
    messageEl.className = `aipi-message aipi-${role}`;

    // Contenedor interior para estilos espec√≠ficos
    const messageInner = document.createElement('div');
    messageInner.className = 'aipi-message-content';

    // Formatear el contenido seg√∫n el rol del mensaje
    if (role === 'assistant') {
      messageInner.innerHTML = formatBotResponse(content);
      // Aplicar contraste autom√°tico
      const bgColor = config.mainColor || '#19c37d';
      messageInner.style.backgroundColor = bgColor;
      messageInner.style.color = getContrastTextColor(bgColor);
    } else {
      messageInner.innerHTML = formatMessage(content);
    }

    // A√±adir avatar para mensajes del asistente
    if (role === 'assistant') {
      const avatar = document.createElement('div');
      avatar.className = 'aipi-message-avatar';
      avatar.style.backgroundColor = config.mainColor;
      avatar.innerHTML = `
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
          <path d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z"></path>
        </svg>
      `;
      messageEl.appendChild(avatar);
    }

    // A√±adir el contenido del mensaje
    messageEl.appendChild(messageInner);

    messagesContainer.appendChild(messageEl);

    // Desplazar al fondo
    messagesContainer.scrollTop = messagesContainer.scrollHeight;
  }

  // Funci√≥n para obtener color de texto con contraste adecuado
  function getContrastTextColor(backgroundColor) {
    if (!backgroundColor || typeof backgroundColor !== 'string') {
      return '#1f2937';
    }

    let hex = backgroundColor.replace('#', '');
    if (hex.length === 3) {
      hex = hex.split('').map(c => c + c).join('');
    }

    if (hex.length !== 6) {
      return '#1f2937';
    }

    const r = parseInt(hex.substr(0, 2), 16);
    const g = parseInt(hex.substr(2, 2), 16);
    const b = parseInt(hex.substr(4, 2), 16);
    const luminance = (0.2126 * r + 0.7152 * g + 0.0722 * b) / 255;

    return luminance < 0.6 ? '#ffffff' : '#1f2937';
  }

  // Formatear mensaje para mostrar enlaces y saltos de l√≠nea
  function formatMessage(text) {
    if (!text) return '';

    // Escapar HTML para seguridad
    let safeText = escapeHTML(text);

    // Convertir URLs en enlaces clicables
    safeText = safeText.replace(
      /(https?:\/\/[^\s]+)/g, 
      '<a href="$1" target="_blank" rel="noopener noreferrer">$1</a>'
    );

    // Convertir saltos de l√≠nea en etiquetas <br>
    safeText = safeText.replace(/\n/g, '<br>');

    return safeText;
  }

  // Mostrar/ocultar indicador de escritura
  function showTypingIndicator(show) {
    const messagesContainer = document.getElementById('aipi-chat-messages');
    if (!messagesContainer) return;

    // Eliminar indicador existente si lo hay
    const existingIndicator = document.getElementById('aipi-typing-indicator');
    if (existingIndicator) {
      existingIndicator.remove();
    }

    if (show) {
      const indicator = document.createElement('div');
      indicator.id = 'aipi-typing-indicator';
      indicator.className = 'aipi-message aipi-assistant';

      const avatar = document.createElement('div');
      avatar.className = 'aipi-message-avatar';
      avatar.style.backgroundColor = config.mainColor;
      avatar.innerHTML = `
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
          <path d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z"></path>
        </svg>
      `;

      const content = document.createElement('div');
      content.className = 'aipi-message-content';
      content.innerHTML = `
        <div class="aipi-typing-animation">
          <span></span>
