        background-color: rgba(0, 0, 0, 0.1);
      }

      /* Layout del cuerpo principal con sidebar (estilo ChatGPT) */
      #aipi-chat-body {
        display: flex;
        flex: 1;
        overflow: hidden;
        position: relative;
        height: calc(100% - 60px);
      }

      /* Sidebar con sugerencias */
      #aipi-chat-sidebar {
        width: 260px;
        background-color: #f9fafb;
        border-right: 1px solid #e5e7eb;
        display: flex;
        flex-direction: column;
        height: 100%;
        overflow-y: auto;
      }

      #aipi-sidebar-header {
        padding: 16px;
        border-bottom: 1px solid #e5e7eb;
      }

      #aipi-sidebar-header h3 {
        font-size: 14px;
        font-weight: 600;
        color: #4b5563;
        margin: 0;
      }

      #aipi-suggested-topics {
        flex: 1;
        overflow-y: auto;
        padding: 8px;
      }

      .aipi-topic {
        display: flex;
        align-items: center;
        padding: 10px 12px;
        margin-bottom: 8px;
        border-radius: 6px;
        cursor: pointer;
        transition: background-color 0.2s;
        background-color: #fff;
        border: 1px solid #e5e7eb;
      }

      .aipi-topic:hover {
        background-color: #f3f4f6;
      }

      .aipi-topic svg {
        color: #6b7280;
        margin-right: 10px;
        flex-shrink: 0;
      }

      .aipi-topic span {
        font-size: 13px;
        line-height: 1.4;
        color: #374151;
        overflow: hidden;
        text-overflow: ellipsis;
        white-space: nowrap;
      }

      #aipi-new-chat {
        padding: 12px;
        border-top: 1px solid #e5e7eb;
      }

      #aipi-new-chat-button {
        display: flex;
        align-items: center;
        justify-content: center;
        width: 100%;
        padding: 8px 12px;
        background-color: #fff;
        border: 1px solid #d1d5db;
        border-radius: 6px;
        font-size: 13px;
        color: #374151;
        cursor: pointer;
        transition: background-color 0.2s;
      }

      #aipi-new-chat-button:hover {
        background-color: #f3f4f6;
      }

      #aipi-new-chat-button svg {
        margin-right: 8px;
      }

      /* √Årea principal de chat */
      #aipi-chat-main {
        flex: 1;
        display: flex;
        flex-direction: column;
        height: 100%;
        overflow: hidden;
      }

      /* √Årea de mensajes */
      #aipi-chat-messages {
        flex: 1;
        overflow-y: auto;
        padding: 20px 0;
        background-color: ${messagesBgColor};
      }

      /* Estilo de mensajes tipo ChatGPT */
      .aipi-message {
        display: flex;
        padding: 14px 60px; /* Padding amplio como ChatGPT */
        position: relative;
        margin: 1px 0; /* Margen m√≠nimo entre mensajes */
      }

      .aipi-message.aipi-user {
        background-color: #f9f9f9; /* Fondo claro para mensajes del usuario */
      }

      .aipi-message.aipi-assistant {
        background-color: #ffffff; /* Fondo blanco para mensajes del asistente */
      }

      .aipi-message-avatar {
        width: 28px;
        height: 28px;
        border-radius: 4px; /* Avatar cuadrado con bordes redondeados como en ChatGPT */
        display: flex;
        align-items: center;
        justify-content: center;
        margin-right: 16px;
        flex-shrink: 0;
      }

      .aipi-message-avatar svg {
        width: 16px;
        height: 16px;
        color: white;
      }

      .aipi-message-content {
        font-size: 16px;
        line-height: 1.6;
        color: ${assistantTextColor};
        word-wrap: break-word;
        overflow-wrap: break-word;
        max-width: 90%;
      }

      .aipi-message.aipi-user .aipi-message-content {
        margin-left: 44px; /* Espacio para alinear con mensajes del asistente */
      }

      /* Enlaces en mensajes */
      .aipi-message-content a {
        color: #2563eb;
        text-decoration: underline;
      }

      .aipi-message-content a:hover {
        text-decoration: none;
      }

      /* √Årea de entrada */
      #aipi-chat-input-area {
        display: flex;
        align-items: center;
        padding: 14px 60px 20px; /* M√°s padding abajo para dar espacio */
        background-color: ${messagesBgColor};
        border-top: 1px solid ${borderColor};
      }

      #aipi-chat-input {
        flex: 1;
        padding: 12px 16px;
        border: 1px solid ${inputBorderColor};
        border-radius: 8px;
        font-size: 16px;
        line-height: 1.5;
        resize: none;
        min-height: 24px;
        max-height: 200px;
        outline: none;
        box-shadow: 0 1px 2px rgba(0, 0, 0, 0.05);
        transition: border-color 0.2s, box-shadow 0.2s;
        background-color: ${inputBgColor};
        color: ${inputTextColor};
      }

      #aipi-chat-input:focus {
        border-color: #bfdbfe;
        box-shadow: 0 0 0 2px rgba(59, 130, 246, 0.3);
      }

      #aipi-chat-send {
        background-color: #3b82f6;
        color: white;
        border: none;
        border-radius: 8px;
        width: 40px;
        height: 40px;
        margin-left: 10px;
        display: flex;
        align-items: center;
        justify-content: center;
        cursor: pointer;
        transition: background-color 0.2s, transform 0.2s;
      }

      #aipi-chat-send:hover:not(:disabled) {
        background-color: #2563eb;
        transform: translateY(-2px);
      }

      #aipi-chat-send:disabled {
        background-color: #cbd5e1;
        cursor: not-allowed;
      }

      #aipi-chat-send svg {
        width: 18px;
        height: 18px;
      }

      /* Animaci√≥n de "Escribiendo..." */
      .aipi-typing-animation {
        display: flex;
        align-items: center;
      }

      .aipi-typing-animation span {
        height: 8px;
        width: 8px;
        margin: 0 2px;
        background-color: #9ca3af;
        border-radius: 50%;
        display: inline-block;
        opacity: 0.7;
      }

      .aipi-typing-animation span:nth-child(1) {
        animation: aipiPulse 1s infinite;
      }

      .aipi-typing-animation span:nth-child(2) {
        animation: aipiPulse 1s infinite 0.2s;
      }

      .aipi-typing-animation span:nth-child(3) {
        animation: aipiPulse 1s infinite 0.4s;
      }

      @keyframes aipiPulse {
        0%, 100% {
          transform: scale(1);
          opacity: 0.7;
        }
        50% {
          transform: scale(1.2);
          opacity: 1;
        }
      }

      /* Estilo para dispositivos m√≥viles */
      @media (max-width: 768px) {
        #aipi-chat-sidebar {
          display: none; /* Ocultar sidebar en m√≥viles */
        }

        .aipi-message {
          padding: 12px 16px;
        }

        #aipi-chat-input-area {
          padding: 12px 16px 16px;
        }

        .aipi-message-content {
          font-size: 15px;
        }
      }
    `;
  }
})();

public/static/form-button.js.content:
/**
 * AIPI Form Button - Bot√≥n flotante para formularios AIPI
 * Este script crea un bot√≥n flotante que permite acceder a los formularios AIPI desde cualquier p√°gina web.
 */
(function() {
  // Configuraci√≥n por defecto
  const DEFAULT_CONFIG = {
    formId: '',
    text: 'Abrir formulario',
    position: 'bottom-right',
    color: '#4a90e2',
    textColor: '#FFFFFF',
    type: 'modal',
    icon: 'form',
    size: 'medium',
    radius: '4px',
  };

  // Obtener configuraci√≥n del script tag
  const scriptTag = document.getElementById('aipi-form-button');
  if (!scriptTag) {
    console.error('No se pudo encontrar la etiqueta script con ID "aipi-form-button"');
    return;
  }

  // Mezclar la configuraci√≥n predeterminada con los atributos data- del script
  const config = {
    ...DEFAULT_CONFIG,
    formId: scriptTag.getAttribute('data-form-id') || DEFAULT_CONFIG.formId,
    text: scriptTag.getAttribute('data-text') || DEFAULT_CONFIG.text,
    position: scriptTag.getAttribute('data-position') || DEFAULT_CONFIG.position,
    color: scriptTag.getAttribute('data-color') || DEFAULT_CONFIG.color,
    textColor: scriptTag.getAttribute('data-text-color') || DEFAULT_CONFIG.textColor,
    type: scriptTag.getAttribute('data-type') || DEFAULT_CONFIG.type,
    icon: scriptTag.getAttribute('data-icon') || DEFAULT_CONFIG.icon,
    size: scriptTag.getAttribute('data-size') || DEFAULT_CONFIG.size,
    radius: scriptTag.getAttribute('data-radius') || DEFAULT_CONFIG.radius,
  };

  // Si no se proporcion√≥ un ID de formulario, mostrar un error
  if (!config.formId) {
    console.error('Se requiere un ID de formulario (data-form-id)');
    return;
  }

  // Inicializar cuando el DOM est√© listo
  document.addEventListener('DOMContentLoaded', initialize);

  /**
   * Inicializar el bot√≥n flotante
   */
  function initialize() {
    addStyles();
    createFloatingButton();
  }

  /**
   * Agrega los estilos CSS necesarios para el bot√≥n flotante
   */
  function addStyles() {
    const styleElement = document.createElement('style');
    styleElement.textContent = `
      /* Estilos para el bot√≥n flotante AIPI */
      .aipi-form-button {
        position: fixed;
        z-index: 9999;
        display: flex;
        align-items: center;
        justify-content: center;
        border: none;
        cursor: pointer;
        box-shadow: 0 2px 12px rgba(0, 0, 0, 0.15);
        transition: all 0.3s ease;
        font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif;
      }

      .aipi-form-button:hover {
        transform: translateY(-2px);
        box-shadow: 0 4px 14px rgba(0, 0, 0, 0.25);
      }

      .aipi-form-button svg {
        margin-right: 8px;
      }

      /* Tama√±os */
      .aipi-form-button.small {
        padding: 6px 12px;
        font-size: 14px;
      }

      .aipi-form-button.medium {
        padding: 8px 16px;
        font-size: 16px;
      }

      .aipi-form-button.large {
        padding: 12px 24px;
        font-size: 18px;
      }

      /* Posiciones */
      .aipi-form-button.bottom-right {
        bottom: 20px;
        right: 20px;
      }

      .aipi-form-button.bottom-left {
        bottom: 20px;
        left: 20px;
      }

      .aipi-form-button.top-right {
        top: 20px;
        right: 20px;
      }

      .aipi-form-button.top-left {
        top: 20px;
        left: 20px;
      }

      /* Modal */
      .aipi-form-modal {
        display: none;
        position: fixed;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        z-index: 10000;
        background-color: rgba(0, 0, 0, 0.5);
        align-items: center;
        justify-content: center;
      }

      .aipi-form-modal-content {
        background-color: white;
        border-radius: 8px;
        width: 90%;
        max-width: 800px;
        max-height: 90vh;
        box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
        position: relative;
        overflow: hidden;
      }

      .aipi-form-modal-close {
        position: absolute;
        top: 10px;
        right: 10px;
        width: 24px;
        height: 24px;
        background: rgba(0, 0, 0, 0.1);
        border-radius: 50%;
        display: flex;
        align-items: center;
        justify-content: center;
        cursor: pointer;
        z-index: 1;
      }

      .aipi-form-modal-close:hover {
        background: rgba(0, 0, 0, 0.2);
      }

      /* Panel deslizante */
      .aipi-form-slidein {
        display: none;
        position: fixed;
        top: 0;
        right: 0;
        width: 100%;
        max-width: 400px;
        height: 100%;
        z-index: 10000;
        box-shadow: -5px 0 15px rgba(0, 0, 0, 0.1);
        transform: translateX(100%);
        transition: transform 0.3s ease;
      }

      .aipi-form-slidein.active {
        transform: translateX(0);
      }

      .aipi-form-slidein-content {
        background-color: white;
        width: 100%;
        height: 100%;
        overflow: auto;
        position: relative;
      }

      .aipi-form-slidein-close {
        position: absolute;
        top: 10px;
        right: 10px;
        width: 24px;
        height: 24px;
        background: rgba(0, 0, 0, 0.1);
        border-radius: 50%;
        display: flex;
        align-items: center;
        justify-content: center;
        cursor: pointer;
        z-index: 1;
      }

      .aipi-form-slidein-close:hover {
        background: rgba(0, 0, 0, 0.2);
      }

      /* Iframe */
      .aipi-form-iframe {
        width: 100%;
        height: 100%;
        border: none;
      }
    `;
    document.head.appendChild(styleElement);
  }

  /**
   * Crea y agrega el bot√≥n flotante al DOM
   */
  function createFloatingButton() {
    const button = document.createElement('button');
    button.className = `aipi-form-button ${config.position} ${config.size}`;
    button.style.backgroundColor = config.color;
    button.style.color = config.textColor; // Usar el color de texto configurado
    button.style.borderRadius = config.radius;

    // Agregar √≠cono si se especific√≥
    if (config.icon && config.icon !== 'none') {
      button.innerHTML = getIconSvg(config.icon) + config.text;
    } else {
      button.textContent = config.text;
    }

    // Agregar evento de clic
    button.addEventListener('click', showForm);

    // Agregar al DOM
    document.body.appendChild(button);
  }

  /**
   * Obtiene el SVG para el √≠cono especificado
   */
  function getIconSvg(icon) {
    const icons = {
      form: '<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><rect x="3" y="3" width="18" height="18" rx="2" ry="2"></rect><line x1="9" y1="9" x2="15" y2="9"></line><line x1="9" y1="13" x2="15" y2="13"></line><line x1="9" y1="17" x2="13" y2="17"></line></svg>',
      contact: '<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><path d="M22 16.92v3a2 2 0 0 1-2.18 2 19.79 19.79 0 0 1-8.63-3.07 19.5 19.5 0 0 1-6-6 19.79 19.79 0 0 1-3.07-8.67A2 2 0 0 1 4.11 2h3a2 2 0 0 1 2 1.72 12.84 12.84 0 0 0 .7 2.81 2 2 0 0 1-.45 2.11L8.09 9.91a16 16 0 0 0 6 6l1.27-1.27a2 2 0 0 1 2.11-.45 12.84 12.84 0 0 0 2.81.7A2 2 0 0 1 22 16.92z"></path></svg>',
      order: '<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><circle cx="9" cy="21" r="1"></circle><circle cx="20" cy="21" r="1"></circle><path d="M1 1h4l2.68 13.39a2 2 0 0 0 2 1.61h9.72a2 2 0 0 0 2-1.61L23 6H6"></path></svg>',
      survey: '<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"></path><polyline points="14 2 14 8 20 8"></polyline><line x1="16" y1="13" x2="8" y2="13"></line><line x1="16" y1="17" x2="8" y2="17"></line><polygon points="10 9 9 9 8 9 9 9"></polygon></svg>',
    };

    return icons[icon] || '';
  }

  /**
   * Muestra el formulario seg√∫n el tipo de visualizaci√≥n configurado
   */
  function showForm() {
    switch (config.type) {
      case 'modal':
        showModalForm();
        break;
      case 'slidein':
        showSlideInForm();
        break;
      case 'redirect':
        // Redirigir a la URL del formulario
        window.location.href = getFormUrl();
        break;
      default:
        showModalForm();
    }
  }

  /**
   * Obtiene la URL del formulario
   */
  function getFormUrl() {
    // Obtener el origen del script para construir la URL completa
    const scriptSrc = scriptTag.src;
    const origin = new URL(scriptSrc).origin;
    return `${origin}/forms/${config.formId}/view`;
  }

  /**
   * Muestra el formulario en una ventana modal
   */
  function showModalForm() {
    // Crear el contenedor modal si no existe
    let modal = document.querySelector('.aipi-form-modal');
    if (!modal) {
      modal = document.createElement('div');
      modal.className = 'aipi-form-modal';

      const modalContent = document.createElement('div');
      modalContent.className = 'aipi-form-modal-content';

      const closeButton = document.createElement('div');
      closeButton.className = 'aipi-form-modal-close';
      closeButton.innerHTML = '<svg xmlns="http://www.w3.org/2000/svg" width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><line x1="18" y1="6" x2="6" y2="18"></line><line x1="6" y1="6" x2="18" y2="18"></line></svg>';
      closeButton.addEventListener('click', closeModal);

      const iframe = document.createElement('iframe');
      iframe.className = 'aipi-form-iframe';
      iframe.src = getFormUrl();

      modalContent.appendChild(closeButton);
      modalContent.appendChild(iframe);
      modal.appendChild(modalContent);
      document.body.appendChild(modal);

      // Cerrar modal al hacer clic fuera
      modal.addEventListener('click', function(e) {
        if (e.target === modal) {
          closeModal();
        }
      });
    }

    // Mostrar modal
    modal.style.display = 'flex';

    // Prevenir scroll del body
    document.body.style.overflow = 'hidden';

    function closeModal() {
      modal.style.display = 'none';
      document.body.style.overflow = '';
    }
  }

  /**
   * Muestra el formulario en un panel deslizante
   */
  function showSlideInForm() {
    // Crear el panel deslizante si no existe
    let slideIn = document.querySelector('.aipi-form-slidein');
    if (!slideIn) {
      slideIn = document.createElement('div');
      slideIn.className = 'aipi-form-slidein';

      const slideInContent = document.createElement('div');
      slideInContent.className = 'aipi-form-slidein-content';

      const closeButton = document.createElement('div');
      closeButton.className = 'aipi-form-slidein-close';
      closeButton.innerHTML = '<svg xmlns="http://www.w3.org/2000/svg" width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><line x1="18" y1="6" x2="6" y2="18"></line><line x1="6" y1="6" x2="18" y2="18"></line></svg>';
      closeButton.addEventListener('click', closeSlideIn);

      const iframe = document.createElement('iframe');
      iframe.className = 'aipi-form-iframe';
      iframe.src = getFormUrl();

      slideInContent.appendChild(closeButton);
      slideInContent.appendChild(iframe);
      slideIn.appendChild(slideInContent);
      document.body.appendChild(slideIn);
    }

    // Mostrar panel deslizante
    slideIn.style.display = 'block';
    setTimeout(() => {
      slideIn.classList.add('active');
    }, 10);

    function closeSlideIn() {
      slideIn.classList.remove('active');
      setTimeout(() => {
        slideIn.style.display = 'none';
      }, 300);
    }
  }
})();

