      #aipi-chat-send:disabled {
        background-color: #d1d5db !important;
        cursor: not-allowed;
      }

      #typing-indicator {
        font-style: italic;
      }
    `;
  }

  // Calcular posici√≥n del bot√≥n seg√∫n la configuraci√≥n
  function getPositionStyle(position) {
    const style = {
      bottom: 'auto',
      right: 'auto',
      top: 'auto',
      left: 'auto'
    };

    switch (position) {
      case 'bottom-right':
        style.bottom = '20px';
        style.right = '20px';
        break;
      case 'bottom-left':
        style.bottom = '20px';
        style.left = '20px';
        break;
      case 'top-right':
        style.top = '20px';
        style.right = '20px';
        break;
      case 'top-left':
        style.top = '20px';
        style.left = '20px';
        break;
      default:
        style.bottom = '20px';
        style.right = '20px';
    }

    return style;
  }
})();

