// Sistema de conocimiento personalizado para cada chatbot
export function buildKnowledgeBase(integration: any, documents: any[], siteContent: any[]): string {
  let knowledgeBase = integration.botBehavior || "Eres un asistente √∫til para este sitio web.";

  console.log(`buildKnowledgeBase Debug: Building knowledge for ${integration.name}`);
  console.log(`buildKnowledgeBase Debug: Documents count: ${documents.length}`);
  console.log(`buildKnowledgeBase Debug: Site content count: ${siteContent.length}`);

  // Agregar informaci√≥n espec√≠fica del sitio web basada en la integraci√≥n
  knowledgeBase += `

INFORMACI√ìN ESPEC√çFICA DEL SITIO WEB:
Nombre del sitio: ${integration.name}
URL: ${integration.url}
Descripci√≥n: ${integration.description || 'No hay descripci√≥n disponible'}

`;

  // Agregar documentos espec√≠ficos subidos por el usuario
  if (documents && documents.length > 0) {
    knowledgeBase += "\n\nDOCUMENTOS Y ARCHIVOS SUBIDOS POR EL USUARIO:\n";
    documents.forEach(doc => {
      if (doc.content) {
        knowledgeBase += `\n--- DOCUMENTO: ${doc.original_name || doc.filename} ---\n`;
        knowledgeBase += `${doc.content}\n\n`;
      }
    });
  }

  // Agregar contenido espec√≠fico del sitio web extra√≠do por scraping
  if (siteContent && siteContent.length > 0) {
    knowledgeBase += "\n\nCONTENIDO EXTRA√çDO DEL SITIO WEB:\n";
    siteContent.forEach(content => {
      if (content.content && content.title) {
        knowledgeBase += `\n--- P√ÅGINA: ${content.title} ---\n`;
        knowledgeBase += `URL: ${content.url}\n`;
        knowledgeBase += `Contenido: ${content.content}\n\n`;
      }
    });
  }

  // Solo agregar informaci√≥n de AIPPS si es la integraci√≥n espec√≠fica de AIPPS
  if (integration.apiKey === 'aipps_web_internal' || integration.name.toLowerCase().includes('aipps')) {
    knowledgeBase += `\n\nINFORMACI√ìN SOBRE AIPPS (solo para consultas sobre la plataforma):
AIPPS es una plataforma de inteligencia artificial que permite crear chatbots y formularios interactivos.
- Caracter√≠sticas: Widget personalizable, soporte multiidioma, integraci√≥n WordPress
- Planes: Gratuito (100 conversaciones/mes), Profesional, Empresarial
- Implementaci√≥n: Registro ‚Üí Crear integraci√≥n ‚Üí Insertar c√≥digo ‚Üí Configurar
`;
  }

  knowledgeBase += `

INSTRUCCIONES ESPEC√çFICAS PARA ESTE CHATBOT:
- Eres el asistente virtual de ${integration.name} (${integration.url})
- Responde √öNICAMENTE bas√°ndote en la informaci√≥n de ESTE sitio web espec√≠fico y los documentos proporcionados
- Si hay documentos subidos, √∫salos como fuente principal de informaci√≥n
- Si te preguntan sobre otros sitios web o servicios no relacionados, explica que solo puedes ayudar con informaci√≥n de ${integration.name}
- Responde siempre en el idioma en que te hablen
- Si no tienes informaci√≥n espec√≠fica, di que puedes ayudar con consultas relacionadas con ${integration.name}
- Mant√©n un tono profesional y servicial
- Identif√≠cate como el asistente de ${integration.name} cuando sea apropiado
`;

  return knowledgeBase;
}

export function extractRelevantInfo(userMessage: string, knowledgeBase: string): string {
  // Palabras clave para identificar temas espec√≠ficos
  const keywords = {
    integration: ['integraci√≥n', 'integrar', 'instalar', 'implementar', 'c√≥digo'],
    forms: ['formulario', 'form', 'campo', 'env√≠o'],
    chat: ['chat', 'chatbot', 'widget', 'conversaci√≥n'],
    api: ['api', 'endpoint', 'webhook', 'developer'],
    pricing: ['precio', 'plan', 'costo', 'pago', 'suscripci√≥n'],
    technical: ['error', 'problema', 'no funciona', 'ayuda t√©cnica']
  };

  const message = userMessage.toLowerCase();
  let relevantSections = [];

  // Identificar secciones relevantes basado en palabras clave
  for (const [topic, words] of Object.entries(keywords)) {
    if (words.some(word => message.includes(word))) {
      relevantSections.push(topic);
    }
  }

  // Extraer secciones relevantes del knowledge base
  if (relevantSections.length > 0) {
    const lines = knowledgeBase.split('\n');
    let relevantInfo = '';
    let capturing = false;

    for (const line of lines) {
      if (line.includes('CARACTER√çSTICAS PRINCIPALES') || 
          line.includes('IMPLEMENTACI√ìN') || 
          line.includes('PLANES DISPONIBLES') ||
          line.includes('SOPORTE T√âCNICO')) {
        capturing = true;
        relevantInfo += line + '\n';
      } else if (capturing && line.trim() === '') {
        capturing = false;
      } else if (capturing) {
        relevantInfo += line + '\n';
      }
    }

    return relevantInfo || knowledgeBase.substring(0, 3000);
  }

  return knowledgeBase.substring(0, 3000);
}

