export function getBaseUrl(): string {
  // Si APP_URL est√° definido, usarlo (m√°xima prioridad)
  if (process.env.APP_URL) {
    return process.env.APP_URL;
  }

  // En un entorno de Railway, usar RAILWAY_STATIC_URL si est√° disponible
  if (process.env.RAILWAY_STATIC_URL) {
    return process.env.RAILWAY_STATIC_URL;
  }

  // En un entorno de Replit, construir la URL basada en la informaci√≥n del entorno
  const replitSlug = process.env.REPL_SLUG;
  const replitOwner = process.env.REPL_OWNER;
  if (replitSlug && replitOwner) {
    return `https://${replitSlug}.${replitOwner}.repl.co`;
  }

  // URL por defecto como √∫ltimo recurso
  return process.env.NODE_ENV === 'production' 
    ? 'https://aipps.ca' 
    : 'http://localhost:5000';
}

