// Funci√≥n para crear o recuperar un producto en Stripe
export async function createOrRetrieveProduct(productInfo: ProductInfo) {
  if (!stripe) return null;

  try {
    // Buscar producto existente por nombre
    const existingProducts = await stripe.products.list({
      active: true
    });

    const existingProduct = existingProducts.data.find(
      product => product.name === productInfo.name
    );

    // Si el producto ya existe, usarlo
    if (existingProduct) {
      return existingProduct;
    }

    // Si no existe, crear el producto
    return await stripe.products.create({
      name: productInfo.name,
      description: productInfo.description,
      metadata: {
        tier: productInfo.tier,
        interactionsLimit: productInfo.interactionsLimit.toString()
      }
    });
  } catch (error) {
    console.error("Error al crear/recuperar producto en Stripe:", error);
    return null;
  }
}

// Funci√≥n para crear o actualizar un precio en Stripe
export async function createOrUpdatePrice(
  product: Stripe.Product, 
  amount: number, 
  interval: 'month' | 'year' = 'month'
) {
  if (!stripe) return null;

  try {
    // Buscar precios existentes para este producto con el intervalo especificado
    const existingPrices = await stripe.prices.list({
      product: product.id,
      active: true
    });

    // Filtrar los precios por intervalo (mensual o anual)
    const matchingPrices = existingPrices.data.filter(price => 
      price.recurring && price.recurring.interval === interval
    );

    // Crear nuevo precio si no existe uno con el intervalo correcto o si el monto ha cambiado
    if (matchingPrices.length === 0 || 
        matchingPrices[0].unit_amount !== amount) {

      // Desactivar precios antiguos con el mismo intervalo si existen y el monto ha cambiado
      if (matchingPrices.length > 0 && 
          matchingPrices[0].unit_amount !== amount) {
        for (const price of matchingPrices) {
          await stripe.prices.update(price.id, { active: false });
        }
      }

      // Crear nuevo precio con el intervalo especificado
      return await stripe.prices.create({
        product: product.id,
        unit_amount: amount,
        currency: 'cad',
        recurring: {
          interval: interval
        },
        metadata: {
          interval: interval,
          isAnnual: interval === 'year' ? 'true' : 'false'
        }
      });
    }

    // Usar el precio existente si el monto y el intervalo son los mismos
    return matchingPrices[0];
  } catch (error) {
    console.error("Error al crear/actualizar precio en Stripe:", error);
    return null;
  }
}

// Crear una sesi√≥n de checkout para suscripci√≥n
export async function createCheckoutSession(
  customerId: string | undefined,
  priceId: string,
  successUrl: string,
  cancelUrl: string
) {
  if (!stripe) return null;

  try {
    const session = await stripe.checkout.sessions.create({
      customer: customerId,
      payment_method_types: ['card'],
      line_items: [
        {
          price: priceId,
          quantity: 1,
        },
      ],
      mode: 'subscription',
      success_url: successUrl,
      cancel_url: cancelUrl,
    });

    return session;
  } catch (error) {
    console.error("Error al crear sesi√≥n de checkout:", error);
    return null;
  }
}

// Funci√≥n para crear o actualizar un cliente en Stripe
export async function createOrUpdateCustomer(
  email: string,
  name: string,
  existingCustomerId?: string
) {
  if (!stripe) return null;

  try {
    // Si ya hay un ID de cliente, actualizar ese cliente
    if (existingCustomerId) {
      return await stripe.customers.update(existingCustomerId, {
        email,
        name,
      });
    }

    // Si no hay ID, buscar si existe un cliente con ese email
    const existingCustomers = await stripe.customers.list({
      email,
      limit: 1,
    });

    if (existingCustomers.data.length > 0) {
      return existingCustomers.data[0];
    }

    // Si no existe, crear nuevo cliente
    return await stripe.customers.create({
      email,
      name,
    });
  } catch (error) {
    console.error("Error al crear/actualizar cliente en Stripe:", error);
    return null;
  }
}

// Recuperar subscripci√≥n
export async function retrieveSubscription(subscriptionId: string) {
  if (!stripe) return null;

  try {
    return await stripe.subscriptions.retrieve(subscriptionId);
  } catch (error) {
    console.error("Error al recuperar subscripci√≥n:", error);
    return null;
  }
}

// Actualizar subscripci√≥n
export async function updateSubscription(subscriptionId: string, data: Stripe.SubscriptionUpdateParams) {
  if (!stripe) return null;

  try {
    return await stripe.subscriptions.update(subscriptionId, data);
  } catch (error) {
    console.error("Error al actualizar subscripci√≥n:", error);
    return null;
  }
}

// Cancelar subscripci√≥n
export async function cancelSubscription(subscriptionId: string) {
  if (!stripe) return null;

  try {
    return await stripe.subscriptions.cancel(subscriptionId);
  } catch (error) {
    console.error("Error al cancelar subscripci√≥n:", error);
    return null;
  }
}

// Manejar eventos de webhook de Stripe
export async function handleWebhookEvent(event: Stripe.Event) {
  if (!stripe) return;

  try {
    // Manejar diferentes tipos de eventos
    switch (event.type) {
      case 'checkout.session.completed':
        const session = event.data.object as Stripe.Checkout.Session;

        // Extraer informaci√≥n de metadata si est√° disponible
        const userId = session.metadata?.userId ? parseInt(session.metadata.userId) : undefined;
        const tier = session.metadata?.tier;

        // Verificar que tenemos datos suficientes
        if (!userId || !session.customer || !session.subscription) {
          console.error('Informaci√≥n incompleta en checkout.session.completed', { userId, customer: session.customer, subscription: session.subscription });
          return;
        }

        // Actualizar la informaci√≥n del usuario en nuestra base de datos
        // Aqu√≠ deber√≠amos guardar el ID de cliente y suscripci√≥n
        console.log(`Usuario ${userId} ha completado el checkout. Customer: ${session.customer}, Subscription: ${session.subscription}`);

        // Esta funci√≥n deber√≠a estar implementada en el storage
        /* await storage.updateUserStripeInfo(userId, {
          stripeCustomerId: session.customer as string,
          stripeSubscriptionId: session.subscription as string
        }); */

        break;

      case 'invoice.payment_succeeded':
        const invoice = event.data.object as Stripe.Invoice;

        // Verificar que tenemos una suscripci√≥n asociada
        if (!invoice.subscription) {
          console.error('invoice.payment_succeeded sin suscripci√≥n asociada', invoice.id);
          return;
        }

        console.log(`Pago exitoso para la suscripci√≥n ${invoice.subscription}`);

        // Aqu√≠ podr√≠as actualizar el estado de la suscripci√≥n a 'active' si es necesario
        break;

      case 'customer.subscription.updated':
        const subscription = event.data.object as Stripe.Subscription;

        console.log(`Suscripci√≥n ${subscription.id} actualizada. Nuevo estado: ${subscription.status}`);

        // Aqu√≠ podr√≠as actualizar el estado y otros detalles de la suscripci√≥n en tu base de datos
        break;

      case 'customer.subscription.deleted':
        const canceledSubscription = event.data.object as Stripe.Subscription;

        console.log(`Suscripci√≥n ${canceledSubscription.id} cancelada`);

        // Aqu√≠ deber√≠as actualizar el estado de la suscripci√≥n en tu base de datos a 'canceled'
        // y posiblemente revertir al usuario a un plan gratuito
        break;

      default:
        console.log(`Evento de Stripe no manejado: ${event.type}`);
    }
  } catch (error) {
    console.error('Error procesando evento de Stripe:', error);
    throw error; // Re-lanzar para manejo en el controlador de ruta
  }
}

export default stripe;

