import { Request, Response, NextFunction } from "express";
import { storage } from "../storage";
import { PRODUCTS } from "../lib/stripe";

// Tipos de interacci√≥n que cuentan para el l√≠mite
export enum InteractionType {
  CHAT = "chat",          // Mensajes de chat
  DOCUMENT = "document",  // Procesamiento de documentos
  ANALYSIS = "analysis",  // An√°lisis de sentimiento, etc.
}

/**
 * Middleware para verificar si el usuario tiene un plan activo y
 * puede realizar la acci√≥n solicitada seg√∫n su nivel de suscripci√≥n
 */
export async function verifySubscription(
  req: Request, 
  res: Response, 
  next: NextFunction
) {
  try {
    // Verificar que el usuario est√° autenticado
    if (!req.userId) {
      return res.status(401).json({ 
        message: "No autorizado. Inicia sesi√≥n para continuar.",
        code: "UNAUTHORIZED"
      });
    }

    // Obtener informaci√≥n de suscripci√≥n del usuario
    const userId = req.userId;
    const subscription = await getUserSubscription(userId);

    // Si no hay una suscripci√≥n, crear una gratuita autom√°ticamente
    if (!subscription) {
      // Crear suscripci√≥n gratuita para el usuario
      await storage.createSubscription({
        userId,
        tier: "free",
        status: "active",
        interactionsLimit: 20,
        startDate: new Date(),
        endDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000), // 30 d√≠as
      });

      // Continuar con la ejecuci√≥n normal
      return next();
    }

    // Verificar que la suscripci√≥n est√© activa
    if (subscription.status !== "active") {
      return res.status(403).json({ 
        message: "Tu suscripci√≥n no est√° activa. Actualiza tu plan para continuar.",
        code: "SUBSCRIPTION_INACTIVE"
      });
    }

    // Verificar que no ha excedido el l√≠mite de interacciones
    // Solo si est√° solicitando una funcionalidad que cuenta como interacci√≥n
    const isInteraction = isCountableInteraction(req.path, req.method);

    if (isInteraction && subscription.interactionsUsed >= subscription.interactionsLimit) {
      return res.status(403).json({ 
        message: "Has alcanzado el l√≠mite de interacciones de tu plan actual. Actualiza tu plan para continuar.",
        code: "INTERACTION_LIMIT_REACHED"
      });
    }

    // Adjuntar la informaci√≥n de suscripci√≥n a la solicitud para uso posterior
    req.subscription = subscription;

    // Continuar con la solicitud
    next();
  } catch (error) {
    console.error("Error verificando suscripci√≥n:", error);
    res.status(500).json({ 
      message: "Error al verificar tu suscripci√≥n. Int√©ntalo nuevamente.",
      code: "SUBSCRIPTION_ERROR"
    });
  }
}

/**
 * Determina si una solicitud cuenta como una interacci√≥n que debe ser limitada
 */
function isCountableInteraction(path: string, method: string): boolean {
  // Comprobar si la ruta corresponde a una interacci√≥n que debe contabilizarse
  const interactionPaths = [
    // Interacciones de chat
    { path: '/api/widget/', method: 'POST' },
    { path: '/api/openai/completion', method: 'POST' },
    { path: '/api/openai/sentiment', method: 'POST' },
    { path: '/api/openai/summarize', method: 'POST' },

    // Procesamiento de documentos
    { path: '/api/documents/upload', method: 'POST' },
    { path: '/api/documents/process', method: 'POST' },

    // An√°lisis
    { path: '/api/analyze/', method: 'POST' },
  ];

  return interactionPaths.some(item => 
    path.includes(item.path) && method === item.method
  );
}

/**
 * Incrementa el contador de interacciones para un usuario
 */
export async function incrementInteractionCount(
  req: Request, 
  interactionType: InteractionType = InteractionType.CHAT
): Promise<boolean> {
  try {
    if (!req.userId || !req.subscription) {
      return false;
    }

    // Incrementar el contador en la base de datos
    await storage.incrementSubscriptionUsage(req.subscription.id);

    return true;
  } catch (error) {
    console.error(`Error incrementando contador de interacciones (${interactionType}):`, error);
    return false;
  }
}

/**
 * Obtiene la informaci√≥n de suscripci√≥n actual de un usuario
 */
export async function getUserSubscription(userId: number) {
  try {
    // Buscar la suscripci√≥n activa del usuario
    const subscriptions = await storage.getUserSubscriptions(userId);

    if (!subscriptions || subscriptions.length === 0) {
      return null;
    }

    // Encontrar la suscripci√≥n activa m√°s reciente
    return subscriptions
      .filter(sub => sub.status === 'active')
      .sort((a, b) => {
        const dateA = a.updatedAt || a.createdAt || new Date(0);
        const dateB = b.updatedAt || b.createdAt || new Date(0);
        return dateB.getTime() - dateA.getTime();
      })[0] || null;
  } catch (error) {
    console.error('Error obteniendo la suscripci√≥n del usuario:', error);
    return null;
  }
}

/**
 * Verifica si un usuario tiene acceso a una caracter√≠stica espec√≠fica basado en su nivel de suscripci√≥n
 */
export function hasFeatureAccess(subscription: any, feature: string): boolean {
  // Lista de caracter√≠sticas por nivel
  const featuresByTier: Record<string, string[]> = {
    free: [
      "widget_bubble",
      "basic_analytics",
    ],
    basic: [
      "widget_bubble",
      "document_upload",
      "basic_analytics",
      "lead_capture",
    ],
    professional: [
      "widget_bubble",
      "widget_fullscreen",
      "document_upload",
      "detailed_analytics",
      "lead_capture",
      "task_automation",
    ],
    enterprise: [
      "widget_bubble",
      "widget_fullscreen",
      "document_upload",
      "detailed_analytics",
      "lead_capture",
      "task_automation",
      "custom_branding",
      "crm_integration",
    ],
  };

  // Si no hay suscripci√≥n o no est√° activa, solo tiene acceso a caracter√≠sticas gratuitas
  if (!subscription || subscription.status !== "active") {
    return featuresByTier.free.includes(feature);
  }

  // Obtener nivel y verificar acceso
  const tier = subscription.tier || "free";

  // Caracter√≠sticas del nivel actual
  const allowedFeatures = featuresByTier[tier] || [];

  // Si es nivel enterprise, conceder acceso a todo
  if (tier === "enterprise") {
    return true;
  }

  return allowedFeatures.includes(feature);
}

/**
 * Convierte un nivel a su l√≠mite de interacciones
 */
export function getInteractionLimitByTier(tier: string): number {
  const limits: Record<string, number> = {
    free: 20,
    basic: 500,
    professional: 2000,
    enterprise: 99999, // "ilimitado"
  };

  return limits[tier.toLowerCase()] || limits.free;
}

// Extender el tipo Request para incluir la suscripci√≥n
declare global {
  namespace Express {
    interface Request {
      subscription?: any;
    }
  }
}

