import { useState, useRef, useEffect } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Bot, X, SendHorizonal } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface Message {
  content: string;
  role: "user" | "assistant";
}

export default function CustomizationAssistant() {
  const [isOpen, setIsOpen] = useState(false);
  const [isSending, setIsSending] = useState(false);
  const [messages, setMessages] = useState<Message[]>([
    {
      role: "assistant",
      content: "¬°Hola! Soy el Asistente de Personalizaci√≥n de AIPI. Puedo ayudarte con cualquier pregunta sobre c√≥mo personalizar o integrar AIPI con tu sitio web. ¬øTe gustar√≠a saber m√°s sobre nuestra nueva funci√≥n de \"Secciones ignoradas\" u otra caracter√≠stica espec√≠fica?"
    }
  ]);
  const [input, setInput] = useState("");
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  // Simula una respuesta del asistente
  const simulateResponse = async (userMessage: string) => {
    // Identificar palabras clave y proporcionar respuestas relevantes
    let response = "";
    const lowerCaseMessage = userMessage.toLowerCase();

    if (lowerCaseMessage.includes("secciones ignoradas") || lowerCaseMessage.includes("ignorar secciones") || lowerCaseMessage.includes("excluir secciones")) {
      response = "La funci√≥n \"Secciones ignoradas\" te permite excluir partes espec√≠ficas de tu sitio web del an√°lisis del chatbot. Esta funci√≥n es muy √∫til para:\n\n1. Mejorar la privacidad, excluyendo informaci√≥n sensible\n2. Obtener respuestas m√°s precisas, eliminando men√∫s o pies de p√°gina irrelevantes\n3. Optimizar el uso de tokens, reduciendo el contenido procesado\n\nPara configurarla:\n\n- En tu panel de control, ve a \"Integraciones\" ‚Üí selecciona tu integraci√≥n\n- Busca la secci√≥n \"Secciones a ignorar\"\n- A√±ade los selectores CSS o IDs de las secciones a ignorar (ej: \"#menu-principal\", \".footer\", \"#sidebar\")\n\nTambi√©n puedes configurarlo mediante c√≥digo:\n\n```\naipi('init', {\n  apiKey: 'TU_API_KEY',\n  // Otras configuraciones...\n  ignoredSections: ['Men√∫ principal', 'Footer', 'Sidebar']\n});\n```";
    } else if (lowerCaseMessage.includes("color") || lowerCaseMessage.includes("tema")) {
      response = "Puedes personalizar los colores y el tema de AIPI desde tu panel de control. Ve a 'Integraciones' ‚Üí selecciona tu integraci√≥n ‚Üí 'Personalizar apariencia'. All√≠ podr√°s ajustar los colores primarios, fondo, texto y otros elementos visuales para que coincidan con tu marca.";
    } else if (lowerCaseMessage.includes("posici√≥n") || lowerCaseMessage.includes("ubicaci√≥n")) {
      response = "La posici√≥n del widget flotante puede configurarse en tu panel de control. Por defecto, aparece en la esquina inferior derecha, pero puedes cambiarlo a la izquierda o ajustar los m√°rgenes seg√∫n tus preferencias. Ve a 'Integraciones' ‚Üí selecciona tu integraci√≥n ‚Üí 'Configuraci√≥n' ‚Üí 'Posici√≥n'.";
    } else if (lowerCaseMessage.includes("wordpress") || lowerCaseMessage.includes("cms")) {
      response = "Para integrar AIPI en WordPress, puedes:\n\n1. Agregar el c√≥digo del script en tu tema (edita footer.php)\n2. Usar un plugin como 'Header and Footer Scripts' para insertar el c√≥digo\n3. Si usas Elementor o Divi, puedes a√±adirlo como un bloque HTML personalizado\n\nRecuerda que tambi√©n puedes configurar 'Secciones ignoradas' para excluir men√∫s, sidebars y footers del contenido analizado por el chatbot.";
    } else if (lowerCaseMessage.includes("mensaje") || lowerCaseMessage.includes("bienvenida")) {
      response = "El mensaje de bienvenida puede personalizarse de tres formas diferentes:\n\n1. Desde el panel de control: 'Integraciones' ‚Üí selecciona tu integraci√≥n ‚Üí 'Configuraci√≥n' ‚Üí 'Mensaje de bienvenida'\n\n2. Mediante el atributo 'data-welcome-message' en el script:\n`<script src=\"TU-URL\" data-welcome-message=\"¬°Hola! ¬øEn qu√© puedo ayudarte?\"></script>`\n\n3. En la configuraci√≥n JavaScript:\n```\naipi('init', {\n  apiKey: 'TU_API_KEY',\n  welcomeMessage: '¬°Hola! ¬øEn qu√© puedo ayudarte?'\n});\n```";
    } else if (lowerCaseMessage.includes("idioma") || lowerCaseMessage.includes("traducci√≥n") || lowerCaseMessage.includes("lenguaje")) {
      response = "AIPI soporta m√∫ltiples idiomas. Puedes configurar el idioma principal y los idiomas adicionales en la secci√≥n 'Configuraci√≥n' ‚Üí 'Idiomas' de tu panel de control. El asistente detectar√° autom√°ticamente el idioma del usuario o respetar√° la configuraci√≥n que establezcas.";
    } else if (lowerCaseMessage.includes("no se muestra") && (lowerCaseMessage.includes("formulario") || lowerCaseMessage.includes("form"))) {
      response = "Si tu formulario no se muestra correctamente, verifica estos puntos:\n\n1. Aseg√∫rate de que el div contenedor tenga el ID correcto: `<div id=\"aipi-form-container\"></div>`\n\n2. El script debe estar colocado ANTES del div contenedor.\n\n3. Revisa que el ID del formulario sea correcto y exista en tu cuenta.\n\n4. Comprueba que el dominio en la URL del script coincida con tu instalaci√≥n de AIPI.\n\n5. C√≥digo completo correcto:\n`<script src=\"https://TU-DOMINIO/static/form-embed.js?id=ID-DEL-FORMULARIO\"></script>`\n`<div id=\"aipi-form-container\"></div>`\n\n6. Si est√°s usando el dominio de Replit, aseg√∫rate de que sea el correcto y est√© activo.";
    } else if (lowerCaseMessage.includes("script") && (lowerCaseMessage.includes("formulario") || lowerCaseMessage.includes("form"))) {
      response = "El script para formularios tiene este formato:\n\n`<script src=\"https://TU-DOMINIO/static/form-embed.js?id=ID-DEL-FORMULARIO\"></script>`\n`<div id=\"aipi-form-container\"></div>`\n\nEs FUNDAMENTAL incluir ambas partes y en ese orden (primero el script, luego el div).\n\nDonde la URL directa del formulario ser√≠a: https://TU-DOMINIO/form/ID-DEL-FORMULARIO\n\nPor ejemplo, con el script:\n`<script src=\"https://aipps.ca/static/form-embed.js?id=registro-a-newsletter-242025\"></script>`\n`<div id=\"aipi-form-container\"></div>`\n\nLa URL directa ser√≠a:\nhttps://aipps.ca/form/registro-a-newsletter-242025";
    } else if (lowerCaseMessage.includes("url") && (lowerCaseMessage.includes("formulario") || lowerCaseMessage.includes("form"))) {
      response = "Para cada formulario que creas, se genera un ID √∫nico (como 'registro-a-newsletter-242025'). La URL directa del formulario se construye as√≠:\n\nhttps://TU-DOMINIO/form/[ID-DEL-FORMULARIO]\n\nEsta URL es directamente accesible y puedes compartirla con tus usuarios sin necesidad de incrustar c√≥digo.\n\nAdem√°s, para incrustarlo en tu web, necesitas AMBOS elementos:\n\n`<script src=\"https://TU-DOMINIO/static/form-embed.js?id=ID-DEL-FORMULARIO\"></script>`\n`<div id=\"aipi-form-container\"></div>`";
    } else if (lowerCaseMessage.includes("formulario") || lowerCaseMessage.includes("form")) {
      response = "Puedes crear y personalizar formularios en la secci√≥n 'Formularios' de tu panel de control. Tenemos varios tipos predise√±ados (contacto, encuesta, registro) que puedes adaptar a tus necesidades.\n\nPara insertar un formulario en tu web:\n\n1. Copia el c√≥digo de incrustaci√≥n completo:\n`<script src=\"https://TU-DOMINIO/static/form-embed.js?id=ID-DEL-FORMULARIO\"></script>`\n`<div id=\"aipi-form-container\"></div>`\n\n2. Pega AMBAS partes del c√≥digo en tu HTML, en ese orden.\n\n3. Tambi√©n puedes compartir la URL directa: https://TU-DOMINIO/form/ID-DEL-FORMULARIO";
    } else if (lowerCaseMessage.includes("error") || lowerCaseMessage.includes("no funciona")) {
      response = "Si est√°s experimentando problemas con la integraci√≥n, verifica:\n\n1. Que el c√≥digo est√© correctamente insertado antes del cierre de </body>\n2. Que tu suscripci√≥n est√© activa\n3. Que no haya bloqueadores de scripts en el navegador\n4. Verifica la consola del navegador (F12) para mensajes de error\n5. Si usas 'Secciones ignoradas', comprueba que los selectores sean correctos\n\nSi el problema persiste, contacta a nuestro soporte t√©cnico con una captura de la consola.";
    } else if (lowerCaseMessage.includes("precio") || lowerCaseMessage.includes("costo") || lowerCaseMessage.includes("plan")) {
      response = "AIPI ofrece diferentes planes seg√∫n tus necesidades:\n\n‚Ä¢ Free: 20 interacciones/d√≠a (gratis)\n‚Ä¢ Basic: 500 interacciones/mes ($50 CAD)\n‚Ä¢ Professional: 2,000 interacciones/mes ($150 CAD)\n‚Ä¢ Enterprise: ilimitado ($500 CAD)\n\nLos planes anuales tienen un descuento del 10-15%. Todos los planes incluyen la funci√≥n 'Secciones ignoradas' para optimizar el uso de tokens.";
    } else if (lowerCaseMessage.includes("id") && lowerCaseMessage.includes("formulario")) {
      response = "El ID de un formulario es una cadena √∫nica que identifica tu formulario en nuestro sistema. Por ejemplo, 'registro-a-newsletter-242025'. Este ID se genera autom√°ticamente cuando creas un formulario y aparece en el c√≥digo de incrustaci√≥n: `<script src=\"https://TU-DOMINIO/static/form-embed.js?id=ID-DEL-FORMULARIO\"></script>`. Tambi√©n puedes encontrar el ID en tu panel de control, en la secci√≥n 'Formularios'.";
    } else if (lowerCaseMessage.includes("documentaci√≥n") || lowerCaseMessage.includes("ayuda") || lowerCaseMessage.includes("gu√≠a")) {
      response = "Puedes encontrar documentaci√≥n detallada sobre AIPI en nuestra p√°gina de documentaci√≥n. All√≠ encontrar√°s gu√≠as sobre:\n\n‚Ä¢ Integraci√≥n de widgets\n‚Ä¢ Comprensi√≥n contextual\n‚Ä¢ Secciones ignoradas (nueva funci√≥n)\n‚Ä¢ Entrenamiento con documentos\n‚Ä¢ Formularios personalizables\n‚Ä¢ Automatizaci√≥n de tareas\n\nPara acceder, ve a la secci√≥n 'Documentaci√≥n' en el men√∫ principal o visita /docs en nuestro sitio.";
    } else if (lowerCaseMessage.includes("widget") || lowerCaseMessage.includes("chat") || lowerCaseMessage.includes("asistente")) {
      response = "AIPI ofrece dos tipos principales de widgets:\n\n1. **Widget Flotante (Burbuja)**: Un peque√±o bot√≥n discreto que aparece en una esquina de tu sitio. Los visitantes hacen clic para abrir un panel de chat compacto.\n\n2. **Pantalla Completa (Estilo ChatGPT)**: Una experiencia inmersiva que ocupa toda la pantalla, ideal para interacciones m√°s extensas.\n\nAmbos tipos soportan la funci√≥n de 'Secciones ignoradas' para personalizar qu√© contenido de tu sitio web analizar√° el asistente.";
    } else {
      response = "AIPI es una plataforma vers√°til para integrar asistentes de IA en tu sitio web. ¬øQu√© te gustar√≠a saber sobre alguna funci√≥n espec√≠fica?\n\nPuedes preguntar sobre:\n‚Ä¢ Secciones ignoradas (nueva funci√≥n)\n‚Ä¢ Integraci√≥n de widgets\n‚Ä¢ Personalizaci√≥n de apariencia\n‚Ä¢ Formularios personalizables\n‚Ä¢ Configuraci√≥n de idiomas\n‚Ä¢ Entrenamiento con documentos\n‚Ä¢ Planes y precios";
    }

    // Simula un retraso para que parezca que est√° pensando
    await new Promise(resolve => setTimeout(resolve, 1500));

    return response;
  };

  const handleSendMessage = async () => {
    if (!input.trim()) return;

    // A√±ade el mensaje del usuario
    const userMessage = { role: "user" as const, content: input.trim() };
    setMessages(prev => [...prev, userMessage]);
    setInput("");
    setIsSending(true);

    try {
      // Simula respuesta de IA
      const response = await simulateResponse(input);

      // A√±ade la respuesta del asistente
      setMessages(prev => [...prev, { role: "assistant", content: response }]);
    } catch (error) {
      toast({
        title: "Error al procesar la solicitud",
        description: "No se pudo obtener una respuesta. Por favor, intenta de nuevo.",
        variant: "destructive"
      });
    } finally {
      setIsSending(false);
    }
  };

  // Scroll al fondo cuando hay nuevos mensajes
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Enfocar el input cuando se abre el chat
  useEffect(() => {
    if (isOpen) {
      inputRef.current?.focus();
    }
  }, [isOpen]);

  // Manejar env√≠o con Enter
  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Formatear el contenido del mensaje con c√≥digo
  const formatMessageContent = (content: string): React.ReactNode => {
    if (!content.includes('`')) return content;

    // Dividir el contenido en segmentos de c√≥digo y texto regular
    const segments = content.split(/(`[^`]+`)/);

    return segments.map((segment, i) => {
      if (segment.startsWith('`') && segment.endsWith('`')) {
        // Es un segmento de c√≥digo, lo formateamos especialmente
        const code = segment.substring(1, segment.length - 1);
        return (
          <code key={i} className="bg-gray-200 dark:bg-gray-700 px-1 rounded font-mono text-sm">
            {code}
          </code>
        );
      }
      // Es texto regular
      return <span key={i}>{segment}</span>;
    });
  };

  return (
    <>
      {/* Bot√≥n flotante para abrir el chat */}
      {!isOpen && (
        <button
          onClick={() => setIsOpen(true)}
          className="fixed bottom-6 right-6 bg-primary text-white p-4 rounded-full shadow-lg hover:bg-primary/90 transition-all z-50"
          aria-label="Abrir asistente de personalizaci√≥n"
        >
          <Bot size={24} />
        </button>
      )}

      {/* Ventana de chat */}
      {isOpen && (
        <Card className="fixed bottom-6 right-6 w-[350px] md:w-[400px] h-[500px] shadow-xl z-50 flex flex-col">
          <div className="bg-primary text-white p-3 flex justify-between items-center">
            <div className="flex items-center gap-2">
              <Bot size={20} />
              <span className="font-medium">Asistente de Personalizaci√≥n</span>
            </div>
            <button 
              onClick={() => setIsOpen(false)}
              className="text-white hover:text-gray-200"
              aria-label="Cerrar asistente"
            >
              <X size={20} />
            </button>
          </div>

          <CardContent className="flex-grow overflow-auto p-4 flex flex-col gap-3">
            {messages.map((msg, index) => (
              <div 
                key={index} 
                className={`flex ${msg.role === "user" ? "justify-end" : "justify-start"}`}
              >
                <div className={`flex gap-2 max-w-[80%] ${msg.role === "user" ? "flex-row-reverse" : "flex-row"}`}>
                  {msg.role === "assistant" && (
                    <Avatar className="h-8 w-8">
                      <AvatarFallback className="bg-primary text-white">AI</AvatarFallback>
                    </Avatar>
                  )}
                  <div 
                    className={`py-2 px-3 rounded-lg whitespace-pre-wrap ${
                      msg.role === "user" 
                        ? "bg-primary text-white rounded-tr-none" 
                        : "bg-gray-100 dark:bg-gray-800 rounded-tl-none"
                    }`}
                  >
                    {formatMessageContent(msg.content)}
                  </div>
                </div>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </CardContent>

          <div className="p-3 border-t border-gray-200 dark:border-gray-700 flex gap-2">
            <Input
              ref={inputRef}
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={handleKeyDown}
              placeholder="Escribe tu pregunta aqu√≠..."
              disabled={isSending}
              className="flex-grow"
            />
            <Button 
              onClick={handleSendMessage} 
              disabled={isSending || !input.trim()}
              size="icon"
            >
              <SendHorizonal size={18} />
            </Button>
          </div>
        </Card>
      )}
    </>
  );
}

