      toast({
        title: "File too large",
        description: "Please select an image smaller than 5MB",
        variant: "destructive",
      });
      return;
    }

    // File type validation
    if (!file.type.startsWith('image/')) {
      toast({
        title: "Invalid file type",
        description: "Please select an image file (JPEG, PNG, etc.)",
        variant: "destructive",
      });
      return;
    }

    setIsUploading(true);

    // Create object URL for preview
    const objectUrl = URL.createObjectURL(file);

    // Update profile context
    updateAvatar(objectUrl);

    // Here you would normally upload to server
    // Simulate server upload delay
    setTimeout(() => {
      setIsUploading(false);
      toast({
        title: "Profile picture updated",
        description: "Your profile picture has been updated successfully",
      });
    }, 1500);
  };

  const handleLogoUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    // File size validation (5MB limit)
    if (file.size > 5 * 1024 * 1024) {
      toast({
        title: "File too large",
        description: "Please select an image smaller than 5MB",
        variant: "destructive",
      });
      return;
    }

    // File type validation
    if (!file.type.startsWith('image/')) {
      toast({
        title: "Invalid file type",
        description: "Please select an image file (JPEG, PNG, etc.)",
        variant: "destructive",
      });
      return;
    }

    setIsLogoUploading(true);

    // Create object URL for preview
    const objectUrl = URL.createObjectURL(file);

    // Update profile context
    updateCompanyLogo(objectUrl);

    // Here you would normally upload to server
    // Simulate server upload delay
    setTimeout(() => {
      setIsLogoUploading(false);
      toast({
        title: "Company logo updated",
        description: "Your company logo has been updated successfully",
      });
    }, 1500);
  };

  const handleUploadClick = () => {
    if (fileInputRef.current) {
      fileInputRef.current.click();
    }
  };

  const handleDeleteAvatar = () => {
    updateAvatar(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
    toast({
      title: "Profile picture removed",
      description: "Your profile picture has been removed",
    });
  };

  // Extract initials safely
  const getInitials = () => {
    if (user && user.fullName && user.fullName.length > 0) {
      return user.fullName.charAt(0).toUpperCase();
    } else if (user && user.username && user.username.length > 0) {
      return user.username.charAt(0).toUpperCase();
    }
    return "U"; // Default fallback
  };

  // Get display name safely
  const getDisplayName = () => {
    if (user) {
      return user.fullName || user.username || "User";
    }
    return "User";
  };

  // Get email safely
  const getEmail = () => {
    return user && user.email ? user.email : "example@aipi.com";
  };

  return (
    <div className="space-y-6">
      <div className="bg-white dark:bg-gray-800 shadow-sm rounded-md p-6">
        <div className="flex flex-col md:flex-row items-start gap-6">
          <div className="relative group">
            <div className="w-32 h-32 rounded-full overflow-hidden bg-primary-100 dark:bg-primary-900 flex items-center justify-center text-primary-600 dark:text-primary-300 text-5xl border-2 border-primary-200 dark:border-primary-800">
              {avatarUrl ? (
                <img src={avatarUrl} alt="User avatar" className="w-full h-full object-cover" />
              ) : (
                getInitials()
              )}
            </div>

            {/* Upload overlay */}
            <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-40 rounded-full flex items-center justify-center transition-all duration-200 opacity-0 group-hover:opacity-100">
              <div className="flex flex-col items-center justify-center space-y-2">
                <button 
                  type="button" 
                  onClick={handleUploadClick}
                  disabled={isUploading}
                  className="rounded-full bg-white p-2 text-gray-700 hover:text-primary-600"
                >
                  {isUploading ? (
                    <div className="animate-spin h-5 w-5 border-2 border-primary-500 border-t-transparent rounded-full" />
                  ) : (
                    <CameraIcon className="h-5 w-5" />
                  )}
                </button>
                {avatarUrl && (
                  <button 
                    type="button" 
                    onClick={handleDeleteAvatar}
                    className="rounded-full bg-white p-2 text-red-500 hover:text-red-700"
                  >
                    <TrashIcon className="h-5 w-5" />
                  </button>
                )}
              </div>
            </div>

            {/* Hidden file input */}
            <input
              type="file"
              ref={fileInputRef}
              onChange={handleFileUpload}
              accept="image/*"
              className="hidden"
            />
          </div>

          <div className="flex-1">
            <h3 className="text-xl font-semibold">{getDisplayName()}</h3>
            <p className="text-gray-500 dark:text-gray-400">{getEmail()}</p>

            <form onSubmit={handleUpdateProfile} className="mt-4 space-y-4">
              <div>
                <Label htmlFor="fullName">Full Name</Label>
                <Input 
                  id="fullName" 
                  name="fullName" 
                  placeholder="Your full name" 
                  defaultValue={user?.fullName || ""} 
                />
              </div>

              <div>
                <Label htmlFor="email">Email</Label>
                <Input 
                  id="email" 
                  name="email" 
                  type="email"
                  placeholder="Your email address" 
                  defaultValue={getEmail()} 
                  disabled
                />
                <p className="text-sm text-gray-500 mt-1">Email cannot be changed</p>
              </div>

              <div className="mt-2">
                <Label htmlFor="companyLogo" className="block mb-2">Company Logo (Optional)</Label>
                <div className="flex items-center space-x-4">
                  {companyLogoUrl ? (
                    <div className="relative group">
                      <div className="w-16 h-16 rounded-md overflow-hidden bg-gray-100 dark:bg-gray-700 border border-gray-200 dark:border-gray-600">
                        <img src={companyLogoUrl} alt="Company logo" className="w-full h-full object-contain" />
                      </div>
                      <button 
                        type="button" 
                        onClick={() => {
                          updateCompanyLogo(null);
                          if (logoInputRef.current) {
                            logoInputRef.current.value = '';
                          }
                          toast({
                            title: "Logo removed",
                            description: "Company logo has been removed",
                          });
                        }}
                        className="absolute -top-2 -right-2 bg-white dark:bg-gray-800 rounded-full p-1 shadow-sm border border-gray-200 dark:border-gray-700 text-red-500 hover:text-red-700"
                      >
                        <TrashIcon className="h-3 w-3" />
                      </button>
                    </div>
                  ) : null}

                  <div className="flex items-center space-x-2">
                    <Button 
                      type="button" 
                      variant="outline" 
                      className="flex items-center"
                      onClick={() => {
                        if (logoInputRef.current) {
                          logoInputRef.current.click();
                        }
                      }}
                      disabled={isLogoUploading}
                    >
                      {isLogoUploading ? (
                        <div className="animate-spin h-4 w-4 border-2 border-primary-500 border-t-transparent rounded-full mr-2" />
                      ) : (
                        <UploadIcon className="h-4 w-4 mr-2" />
                      )}
                      {companyLogoUrl ? "Change Logo" : "Upload Logo"}
                    </Button>
                    <span className="text-sm text-gray-500">
                      Recommended size: 500x500px (Max 5MB)
                    </span>
                  </div>
                </div>
                <input 
                  id="companyLogo" 
                  type="file" 
                  ref={logoInputRef}
                  accept="image/*" 
                  className="hidden" 
                  onChange={handleLogoUpload}
                />
              </div>

              <div className="pt-4">
                <Button type="submit" className="mr-2">
                  Update Profile
                </Button>
                <Button variant="outline" type="button">
                  Change Password
                </Button>
              </div>
            </form>
          </div>
        </div>
      </div>

      <div className="bg-white dark:bg-gray-800 shadow-sm rounded-md p-6">
        <h3 className="text-lg font-medium mb-4">Account API Key</h3>
        <p className="text-gray-500 dark:text-gray-400 mb-4">
          This is your personal API key for accessing the AIPPS API. Keep it secure.
        </p>
        <div className="flex items-center space-x-2">
          <Input 
            value="aipps_demo1234567890"
            readOnly
            className="font-mono"
          />
          <Button 
            variant="outline" 
            size="sm"
            onClick={handleCopyApiKey}
          >
            <CopyIcon className="h-4 w-4 mr-2" />
            {copied ? "Copied!" : "Copy"}
          </Button>
        </div>
      </div>
    </div>
  );
}

client/src/components/stat-card.tsx.content:
import { ReactNode } from "react";
import { Card, CardContent } from "@/components/ui/card";

interface StatCardProps {
  title: string;
  value: string | number;
  change?: number;
  icon: ReactNode;
  color: "primary" | "secondary" | "accent";
}

export default function StatCard({ title, value, change, icon, color }: StatCardProps) {
  const getColorClasses = () => {
    switch (color) {
      case "secondary":
        return "bg-green-100 dark:bg-green-900 text-green-500";
      case "accent":
        return "bg-purple-100 dark:bg-purple-900 text-purple-500";
      case "primary":
      default:
        return "bg-primary-100 dark:bg-primary-900 text-primary-500";
    }
  };

  const getChangeColor = () => {
    if (!change) return "";
    return change > 0 ? "text-green-500" : "text-red-500";
  };

  const changeIcon = change && change > 0 ? (
    <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 10l7-7m0 0l7 7m-7-7v18"></path>
    </svg>
  ) : (
    <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 14l-7 7m0 0l-7-7m7 7V3"></path>
    </svg>
  );

  return (
    <Card>
      <CardContent className="p-6">
        <div className="flex items-center">
          <div className={`p-3 rounded-full ${getColorClasses()}`}>
            {icon}
          </div>
          <div className="ml-4">
            <p className="text-sm font-medium text-gray-500 dark:text-gray-400">{title}</p>
            <p className="text-2xl font-semibold text-gray-900 dark:text-white">{value}</p>
          </div>
        </div>

        {change && (
          <div className="mt-4">
            <div className="flex items-center text-sm">
              <span className={`flex items-center ${getChangeColor()}`}>
                {changeIcon}
                {Math.abs(change).toFixed(1)}%
              </span>
              <span className="text-gray-500 dark:text-gray-400 ml-2">vs last month</span>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/upgrade-plan-modal.content:
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Crown, ArrowRight, X, BarChart3 } from "lucide-react";
import { useTranslation } from "react-i18next";
import { useLocation } from "wouter";
import { usePlanLimits } from "@/hooks/use-plan-limits";

interface UpgradePlanModalProps {
  isOpen: boolean;
  onClose: () => void;
  limitType: 'integrations' | 'forms' | 'conversations' | 'general';
  currentLimit?: number;
  planName?: string;
}

export default function UpgradePlanModal({
  isOpen,
  onClose,
  limitType,
  currentLimit,
  planName = "B√°sico"
}: UpgradePlanModalProps) {
  const { t } = useTranslation();
  const [, navigate] = useLocation();
  const { limits: limitsData } = usePlanLimits();

  const handleUpgrade = () => {
    navigate('/pricing');
    onClose();
  };

  // Determinar el plan actual y los beneficios del pr√≥ximo plan
  const getPlanInfo = () => {
    const currentPlanType = planName?.toLowerCase().includes('profesional') ? 'professional' :
                           planName?.toLowerCase().includes('startup') ? 'startup' :
                           planName?.toLowerCase().includes('empresarial') ? 'enterprise' : 'basic';

    // Beneficios espec√≠ficos seg√∫n el plan actual
    const getNextPlanBenefits = () => {
      switch (currentPlanType) {
        case 'basic':
          return [
            t('upgrade.benefits.startup.conversations', '2,000 conversations/month'),
            t('upgrade.benefits.startup.forms', '5 customizable forms'),
            t('upgrade.benefits.startup.widgets', 'Bubble + fullscreen widgets'),
            t('upgrade.benefits.startup.sites', 'Up to 3 websites'),
            t('upgrade.benefits.startup.analytics', 'Advanced analytics')
          ];
        case 'startup':
          return [
            t('upgrade.benefits.professional.conversations', '10,000 conversations/month'),
            t('upgrade.benefits.professional.forms', 'Unlimited forms'),
            t('upgrade.benefits.professional.sites', 'Unlimited websites'),
            t('upgrade.benefits.professional.automation', 'Basic automations'),
            t('upgrade.benefits.professional.crm', 'CRM integration')
          ];
        case 'professional':
          return [
            t('upgrade.benefits.enterprise.conversations', 'Unlimited conversations'),
            t('upgrade.benefits.enterprise.all', 'Everything included'),
            t('upgrade.benefits.enterprise.ai', 'AI automations'),
            t('upgrade.benefits.enterprise.support', '24/7 dedicated support'),
            t('upgrade.benefits.enterprise.manager', 'Account manager')
          ];
        default:
          return [
            t('upgrade.benefits.general.integrations', 'More integrations and forms'),
            t('upgrade.benefits.general.analytics', 'Advanced analytics and reports'),
            t('upgrade.benefits.general.support', 'Priority support'),
            t('upgrade.benefits.general.customization', 'Complete customization')
          ];
      }
    };

    return { currentPlanType, benefits: getNextPlanBenefits() };
  };

  const getLimitMessage = () => {
    switch (limitType) {
      case 'integrations':
        return {
          message: t('upgrade.integrations.message', `Tu ${planName} ha alcanzado el l√≠mite de integraciones.`, { planName }),
          description: t('upgrade.integrations.description', 'Para conectar m√°s servicios y automatizar tu negocio, necesitas actualizar tu plan.')
        };
      case 'forms':
        return {
          message: t('upgrade.forms.message', `Tu ${planName} ha alcanzado el l√≠mite de formularios.`, { planName }),
          description: t('upgrade.forms.description', 'Para crear m√°s formularios personalizados y capturar m√°s leads, actualiza tu plan.')
        };
      case 'conversations':
        return {
          message: t('upgrade.conversations.message', `Tu ${planName} ha alcanzado el l√≠mite de conversaciones.`, { planName }),
          description: t('upgrade.conversations.description', 'Para manejar m√°s conversaciones con tus clientes, necesitas actualizar tu plan.')
        };
      default:
        return {
          message: t('upgrade.general.message', `Tu ${planName} ha alcanzado sus l√≠mites.`, { planName }),
          description: t('upgrade.general.description', 'Para acceder a m√°s funcionalidades y expandir tu negocio, actualiza tu plan.')
        };
    }
  };

  const { message, description } = getLimitMessage();
  const { benefits } = getPlanInfo();

  // Obtener datos de progreso actual para el tipo de l√≠mite espec√≠fico
  const getCurrentProgress = () => {
    if (!limitsData) return null;

    switch (limitType) {
      case 'integrations':
        return {
          used: limitsData.limits.integrations.used,
          limit: limitsData.limits.integrations.limit,
          percentage: limitsData.limits.integrations.limit === -1 ? 0 : 
                     Math.round((limitsData.limits.integrations.used / limitsData.limits.integrations.limit) * 100),
          isUnlimited: limitsData.limits.integrations.limit === -1
        };
      case 'forms':
        return {
          used: limitsData.limits.forms.used,
          limit: limitsData.limits.forms.limit,
          percentage: limitsData.limits.forms.limit === -1 ? 0 : 
                     Math.round((limitsData.limits.forms.used / limitsData.limits.forms.limit) * 100),
          isUnlimited: limitsData.limits.forms.limit === -1
        };
      case 'conversations':
        return {
          used: limitsData.limits.conversations.used,
          limit: limitsData.limits.conversations.limit,
          percentage: Math.round((limitsData.limits.conversations.used / limitsData.limits.conversations.limit) * 100),
          isUnlimited: false
        };
      default:
        return null;
    }
  };

  const progressData = getCurrentProgress();

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <div className="flex items-center gap-3">
            <div className="flex items-center justify-center w-12 h-12 rounded-full bg-gradient-to-r from-yellow-400 to-orange-500">
              <Crown className="w-6 h-6 text-white" />
            </div>
            <DialogTitle className="text-xl font-semibold">
              {t('upgrade.modal.title', 'You have reached your plan limit')}
            </DialogTitle>
          </div>
        </DialogHeader>

        <div className="space-y-4 pt-4">
          <div className="space-y-2">
            <p className="text-sm text-muted-foreground leading-relaxed">
              {message}
            </p>
            <p className="text-sm text-muted-foreground leading-relaxed">
              {description}
            </p>
          </div>

          {/* Progreso visual de l√≠mites */}
          {progressData && (
            <div className="bg-gradient-to-r from-red-50 to-orange-50 dark:from-red-950/20 dark:to-orange-950/20 rounded-lg p-4 border border-red-200/50 dark:border-red-800/50">
              <div className="flex items-center gap-2 mb-3">
                <BarChart3 className="w-4 h-4 text-red-600 dark:text-red-400" />
                <span className="text-sm font-medium text-red-700 dark:text-red-300">
                  {t('upgrade.progress.title', 'Uso actual:')}
                </span>
              </div>

              <div className="space-y-2">
                <div className="flex justify-between text-xs">
                  <span className="text-red-600 dark:text-red-400">
                    {progressData.isUnlimited 
                      ? t('upgrade.progress.unlimited', `${progressData.used} utilizados`)
                      : t('upgrade.progress.usage', `${progressData.used} de ${progressData.limit} utilizados`)
                    }
                  </span>
                  {!progressData.isUnlimited && (
                    <span className="text-red-600 dark:text-red-400 font-medium">
                      {progressData.percentage}%
                    </span>
                  )}
                </div>

                {!progressData.isUnlimited && (
                  <Progress 
                    value={progressData.percentage} 
                    className="h-2 bg-red-100 dark:bg-red-900/30"
                  />
                )}

                <p className="text-xs text-red-600 dark:text-red-400 mt-2">
                  {limitType === 'integrations' && t('upgrade.progress.integrations', 'Has alcanzado el l√≠mite m√°ximo de integraciones')}
                  {limitType === 'forms' && t('upgrade.progress.forms', 'Has alcanzado el l√≠mite m√°ximo de formularios')}
                  {limitType === 'conversations' && t('upgrade.progress.conversations', 'Has alcanzado el l√≠mite m√°ximo de conversaciones')}
                </p>
              </div>
            </div>
          )}

          <div className="bg-gradient-to-r from-blue-50 to-purple-50 dark:from-blue-950/20 dark:to-purple-950/20 rounded-lg p-4 border border-blue-200/50 dark:border-blue-800/50">
            <div className="flex items-center gap-2 mb-2">
              <Crown className="w-4 h-4 text-blue-600 dark:text-blue-400" />
              <span className="text-sm font-medium text-blue-700 dark:text-blue-300">
                {t('upgrade.benefits.title', 'Beneficios de actualizar:')}
              </span>
            </div>
            <ul className="text-xs text-blue-600 dark:text-blue-400 space-y-1">
              {benefits.map((benefit, index) => (
                <li key={index}>‚Ä¢ {benefit}</li>
              ))}
            </ul>
          </div>

          <div className="flex gap-3 pt-2">
            <Button
              onClick={handleUpgrade}
              className="flex-1 bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700"
            >
              {t('upgrade.button.primary', 'Ver planes')}
              <ArrowRight className="w-4 h-4 ml-2" />
            </Button>
            <Button
              variant="outline"
              onClick={onClose}
              className="px-6"
            >
              {t('upgrade.button.secondary', 'M√°s tarde')}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

