import { createContext, useContext, useState, useEffect, ReactNode } from "react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface User {
  id: number;
  username: string;
  email: string;
  fullName?: string;
}

interface AuthContextType {
  user: User | null;
  isAuthenticated: boolean;
  isLoading: boolean;
  login: (username: string, password: string) => Promise<void>;
  logout: () => Promise<void>;
  refreshAuth: () => Promise<boolean>;
}

const AuthContext = createContext<AuthContextType>({
  user: null,
  isAuthenticated: false,
  isLoading: true,
  login: async () => {},
  logout: async () => {},
  refreshAuth: async () => false,
});

export const useAuth = () => useContext(AuthContext);

interface AuthProviderProps {
  children: ReactNode;
}

export const AuthProvider = ({ children }: AuthProviderProps) => {
  const [user, setUser] = useState<User | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isRefreshing, setIsRefreshing] = useState(false); // Control para evitar peticiones simult√°neas
  const [lastRefreshed, setLastRefreshed] = useState(0); // Timestamp de la √∫ltima actualizaci√≥n
  const { toast } = useToast();

  // Verificar si el token ha expirado
  const isTokenExpired = (token: string): boolean => {
    try {
      const base64Url = token.split('.')[1];
      const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
      const jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
      }).join(''));

      const { exp } = JSON.parse(jsonPayload);
      const expired = Date.now() >= exp * 1000;

      if (expired) {
        console.log("Token expirado. Fecha de expiraci√≥n:", new Date(exp * 1000));
      }

      return expired;
    } catch (error) {
      console.error("Error verificando expiraci√≥n del token:", error);
      return true; // En caso de error, asumir que el token es inv√°lido
    }
  };

  // Funci√≥n para verificar y actualizar el estado de autenticaci√≥n
  const refreshAuth = async (): Promise<boolean> => {
    try {
      // Control para evitar m√∫ltiples peticiones simult√°neas
      if (isRefreshing) {
        console.log("Ya hay una verificaci√≥n en curso. Omitiendo...");
        return !!user; // Devolver el estado actual
      }

      // Control para limitar la frecuencia de peticiones (m√≠nimo 30 segundos entre peticiones)
      const now = Date.now();
      const timeSinceLastRefresh = now - lastRefreshed;
      if (timeSinceLastRefresh < 30000 && lastRefreshed > 0) {
        console.log(`√öltima verificaci√≥n hace ${Math.round(timeSinceLastRefresh/1000)}s. Omitiendo...`);
        return !!user; // Devolver el estado actual si verificamos hace menos de 30 segundos
      }

      setIsRefreshing(true);
      setIsLoading(true);

      console.log("Verificando estado de autenticaci√≥n...");

      // Verificar si hay un token en localStorage
      const authToken = localStorage.getItem('auth_token');

      if (!authToken) {
        console.log("No hay token almacenado.");
        setUser(null);
        setLastRefreshed(now);
        return false;
      }

      // Verificar si el token ha expirado
      if (isTokenExpired(authToken)) {
        console.log("El token ha expirado. Cerrando sesi√≥n...");
        // Limpiar token expirado
        localStorage.removeItem('auth_token');
        setUser(null);

        // Mostrar mensaje amigable
        toast({
          title: "Sesi√≥n expirada",
          description: "Tu sesi√≥n ha expirado. Por favor, inicia sesi√≥n nuevamente.",
          variant: "default"
        });

        setLastRefreshed(now);
        return false;
      }

      // Si el token es v√°lido, intentar obtener datos del usuario
      const headers: Record<string, string> = {
        'Authorization': `Bearer ${authToken}`
      };

      const baseUrl = import.meta.env.VITE_API_BASE_URL || '';
const response = await fetch(`${baseUrl}/api/auth/me`, {
        credentials: "include",
        cache: "no-store",
        headers: headers
      });

      console.log("Estado de la respuesta:", response.status);

      if (response.ok) {
        const userData = await response.json();
        console.log("Usuario autenticado correctamente");
        setUser(userData);
        setLastRefreshed(now);
        return true;
      } else {
        console.log("Sesi√≥n inv√°lida o expirada.");

        // Si el error es de autenticaci√≥n, limpiar el token
        if (response.status === 401) {
          localStorage.removeItem('auth_token');
          toast({
            title: "Sesi√≥n finalizada",
            description: "Tu sesi√≥n ha finalizado. Por favor, inicia sesi√≥n nuevamente.",
            variant: "default"
          });
        }

        setUser(null);
        setLastRefreshed(now);
        return false;
      }
    } catch (error) {
      console.error("Error verificando autenticaci√≥n:", error);
      setUser(null);
      return false;
    } finally {
      setIsLoading(false);
      setIsRefreshing(false);
    }
  };

  // Verificar autenticaci√≥n al cargar la aplicaci√≥n
  useEffect(() => {
    refreshAuth();

    // REMOVED: Periodic session check to prevent 429 rate limiting
    // Session is managed on-demand by components as needed

    return () => {}; // No cleanup needed
  }, []);

  // Login function
  const login = async (username: string, password: string) => {
    // Actualizar el timestamp de √∫ltima actualizaci√≥n al iniciar sesi√≥n
    setLastRefreshed(Date.now());
    try {
      console.log("Intentando iniciar sesi√≥n para el usuario:", username);

      // Usar apiRequest para el login
      const response = await apiRequest("POST", "/api/auth/login", {
        username,
        password,
      });

      // Obtener los datos del usuario y el token
      const userData = await response.json();
      console.log("Usuario autenticado:", userData);

      // Guardar el token en localStorage si viene en la respuesta
      if (userData.token) {
        localStorage.setItem('auth_token', userData.token);
        console.log("Token guardado en localStorage:", userData.token.substring(0, 20) + "...");

        // Eliminar el token del objeto de usuario para no guardarlo en el estado
        const { token, ...userWithoutToken } = userData;
        setUser(userWithoutToken);

        // Mostrar mensaje de bienvenida
        toast({
          title: "Inicio de sesi√≥n exitoso",
          description: `Bienvenido, ${userData.username}`,
        });
      } else {
        console.warn("No se recibi√≥ token en la respuesta de login");
        setUser(userData);
      }
    } catch (error) {
      console.error("Login error:", error);
      // Mostrar mensaje de error
      toast({
        title: "Error de inicio de sesi√≥n",
        description: "Credenciales incorrectas o servidor no disponible",
        variant: "destructive"
      });
      throw error;
    }
  };

  // Logout function
  const logout = async () => {
    try {
      await apiRequest("POST", "/api/auth/logout", {});
      // Eliminar el token del localStorage
      localStorage.removeItem('auth_token');
      setUser(null);

      toast({
        title: "Sesi√≥n cerrada",
        description: "Has cerrado sesi√≥n correctamente",
      });

      // Redirigir al dashboard/login - usando window.location para la navegaci√≥n
      window.location.href = '/';
    } catch (error) {
      // Eliminar el token del localStorage incluso si hay error
      localStorage.removeItem('auth_token');
      console.error("Logout error:", error);
      setUser(null);

      // Redirigir al dashboard/login incluso en caso de error
      window.location.href = '/';
    }
  };

  const value = {
    user,
    isAuthenticated: !!user,
    isLoading,
    login,
    logout,
    refreshAuth,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

