import { useMutation, useQuery } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';

interface FeatureAccessResponse {
  hasAccess: boolean;
  currentPlan: string;
  requiredPlan?: string;
  requiredPlanName?: string;
  upgradeMessage?: string;
  feature: string;
}

interface FeatureAccessRequest {
  feature: string;
}

/**
 * Hook para verificar el acceso a una caracter√≠stica espec√≠fica
 */
export function useFeatureAccess(feature: string) {
  return useQuery({
    queryKey: ['feature-access', feature],
    queryFn: async () => {
      const response = await apiRequest('POST', '/api/features/check-access', { feature });
      return response.json() as Promise<FeatureAccessResponse>;
    },
    staleTime: 5 * 60 * 1000, // 5 minutos
    enabled: !!feature
  });
}

/**
 * Hook para verificar m√∫ltiples caracter√≠sticas
 */
export function useMultipleFeatureAccess(features: string[]) {
  return useQuery({
    queryKey: ['multiple-feature-access', features],
    queryFn: async () => {
      const results = await Promise.all(
        features.map(async (feature) => {
          try {
            const response = await apiRequest('POST', '/api/features/check-access', { feature });
            const result = await response.json();
            return { feature, ...result };
          } catch (error) {
            console.error(`Error checking access for feature ${feature}:`, error);
            return { 
              feature, 
              hasAccess: false, 
              currentPlan: 'basic',
              error: true 
            };
          }
        })
      );

      return results.reduce((acc, result) => {
        acc[result.feature] = result;
        return acc;
      }, {} as Record<string, FeatureAccessResponse & { error?: boolean }>);
    },
    staleTime: 5 * 60 * 1000, // 5 minutos
    enabled: features.length > 0
  });
}

/**
 * Mutation para verificar acceso a caracter√≠sticas bajo demanda
 */
export function useCheckFeatureAccess() {
  return useMutation({
    mutationFn: async (request: FeatureAccessRequest) => {
      const response = await apiRequest('POST', '/api/features/check-access', request);
      return response.json() as Promise<FeatureAccessResponse>;
    }
  });
}

/**
 * Hook que combina la verificaci√≥n de acceso con funciones helper
 */
export function useFeatureCheck(feature: string) {
  const { data, isLoading, error } = useFeatureAccess(feature);

  const hasAccess = data?.hasAccess ?? false;
  const needsUpgrade = !hasAccess && !isLoading && !error;

  return {
    hasAccess,
    needsUpgrade,
    currentPlan: data?.currentPlan,
    requiredPlan: data?.requiredPlan,
    requiredPlanName: data?.requiredPlanName,
    upgradeMessage: data?.upgradeMessage,
    featureName: data?.feature,
    isLoading,
    error
  };
}

