    name: "",
    description: "",
    tier: "free",
    interactionsLimit: 20,
    planId: "",
    price: 0,
    priceDisplay: "$0 CAD",
    currency: "CAD",
    billingPeriod: "monthly",
    features: [] as string[],
    popular: false,
    available: true
  });

  const [editPricingPlanData, setEditPricingPlanData] = useState({
    id: 0,
    name: "",
    description: "",
    tier: "",
    interactionsLimit: 0,
    planId: "",
    price: 0,
    priceDisplay: "",
    currency: "CAD",
    billingPeriod: "monthly",
    features: [] as string[],
    popular: false,
    available: true
  });

  // Query para obtener c√≥digos de descuento
  const { data: discountCodes, isLoading: isLoadingDiscountCodes, refetch: refetchDiscountCodes } = useQuery({
    queryKey: ["/api/discount-codes"],
