        upgradeModal.handlePlanLimitError(error.message);
      } else {
        toast({
          title: "Error al crear la integraci√≥n",
          description: error.message || "Ha ocurrido un error al crear la integraci√≥n",
          variant: "destructive",
        });
      }
    },
  });

