    doc.setFont('helvetica', 'normal');
    yPosition = addTextWithWrap(t("documentation.contextual_understanding_content"), margin, yPosition, pageWidth - 2 * margin);
    yPosition += 15;

    // 5. Document Training
    checkNewPage(40);
