    yPosition += 15;

    doc.setFontSize(12);
    doc.setFont('helvetica', 'normal');
    yPosition = addTextWithWrap(t("documentation.support_content"), margin, yPosition, pageWidth - 2 * margin);

    // Pie de p√°gina en todas las p√°ginas
    const totalPages = doc.getNumberOfPages();
    for (let i = 1; i <= totalPages; i++) {
      doc.setPage(i);
      doc.setFontSize(8);
      doc.setFont('helvetica', 'normal');
      doc.text(`${t('documentation.page') || 'P√°gina'} ${i} ${t('documentation.of') || 'de'} ${totalPages}`, pageWidth - margin, 285, { align: 'right' });
      doc.text(`AIPPS - ${t("documentation.title")}`, margin, 285);
    }

    // Descargar el PDF
    const fileName = `aipps-documentacion-${currentLanguage}.pdf`;
    doc.save(fileName);
  };

  return (
    <div className="min-h-screen flex flex-col">
      <Header />

      <main className="flex-grow">
        <section className="py-12">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
            <div className="flex justify-center items-center mb-6">
