  const { t } = useTranslation();
  const [copied, setCopied] = useState(false);
  const [messages, setMessages] = useState<{content: string, role: 'user' | 'assistant'}[]>([
    {
      role: 'assistant',
      content: t('forms.assistant_welcome', 'Hello, I am your AIPI assistant. I am here to answer any questions about form customization and integration with your website. How can I help you?')
    }
  ]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const chatEndRef = useRef<HTMLDivElement>(null);
  const { toast } = useToast();

  // URL para el script de integraci√≥n
  // Usar window.location.origin para obtener la URL base del sitio
  const formEmbedCode = `<!-- C√≥digo de ejemplo para integrar un formulario de AIPI -->
<script src="${window.location.origin}/static/form-embed.js?id=tu_id_de_formulario"></script>
<div id="aipi-form-container"></div>`;

  // Funci√≥n para copiar c√≥digo al portapapeles
  const copyToClipboard = (code: string) => {
    navigator.clipboard.writeText(code);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  // Funci√≥n para enviar mensaje al asistente
  const sendMessage = async () => {
    if (!inputValue.trim() || isLoading) return;

    // A√±adir mensaje del usuario
    const userMessage = { content: inputValue, role: 'user' as const };
    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsLoading(true);

    try {
      // Simulamos una respuesta del asistente (en producci√≥n, esto llamar√≠a a una API)
      setTimeout(() => {
        let botResponse = '';

        if (inputValue.toLowerCase().includes('personalizar') || inputValue.toLowerCase().includes('personalizaci√≥n')) {
          botResponse = 'Puedes personalizar tus formularios de varias maneras:\n\n1. Cambia los colores para que coincidan con tu marca\n2. Modifica el estilo y tama√±o de fuente\n3. Ajusta el espaciado y bordes\n4. Personaliza los textos de los botones y mensajes\n5. A√±ade tu logotipo\n\nTodo esto lo puedes hacer desde el editor de formularios en tu dashboard.';
        } else if (inputValue.toLowerCase().includes('integrar') || inputValue.toLowerCase().includes('integraci√≥n')) {
          botResponse = `Para integrar un formulario en tu sitio web, debes:\n\n1. Crear y publicar tu formulario desde el dashboard\n2. Copiar el c√≥digo de integraci√≥n que se proporciona\n3. Pegar este c√≥digo en tu sitio web donde quieras que aparezca el formulario\n\nEl c√≥digo se ver√° as√≠:\n\`\`\`html\n${formEmbedCode}\n\`\`\`\n\nDonde "tu_id_de_formulario" ser√° reemplazado por el ID √∫nico de tu formulario.`;
        } else if (inputValue.toLowerCase().includes('campos') || inputValue.toLowerCase().includes('field')) {
          botResponse = 'AIPI soporta diversos tipos de campos para tus formularios:\n\n‚Ä¢ Texto corto\n‚Ä¢ Texto largo (√°rea de texto)\n‚Ä¢ Email\n‚Ä¢ N√∫mero\n‚Ä¢ Tel√©fono\n‚Ä¢ Fecha\n‚Ä¢ Hora\n‚Ä¢ Selecci√≥n (dropdown)\n‚Ä¢ Botones de radio\n‚Ä¢ Casillas de verificaci√≥n\n‚Ä¢ Archivos\n‚Ä¢ Direcci√≥n\n‚Ä¢ URL\n‚Ä¢ Consentimiento\n\nPuedes a√±adir, eliminar y reordenar estos campos seg√∫n tus necesidades.';
        } else if (inputValue.toLowerCase().includes('url') || inputValue.toLowerCase().includes('link')) {
          botResponse = `La URL correcta para el script de integraci√≥n de formularios es:\n\n\`\`\`html\n<script src="${window.location.origin}/static/form-embed.js?id=tu_id_de_formulario"></script>\n\`\`\`\n\nDonde "tu_id_de_formulario" debe ser reemplazado por el slug o ID espec√≠fico de tu formulario. Este ID lo puedes encontrar en la p√°gina de detalles del formulario en tu dashboard.`;
        } else if (inputValue.toLowerCase().includes('respuestas') || inputValue.toLowerCase().includes('datos')) {
          botResponse = 'Las respuestas de tus formularios se almacenan de forma segura en tu cuenta de AIPI. Puedes:\n\n‚Ä¢ Verlas en tiempo real desde el dashboard\n‚Ä¢ Exportarlas en formato CSV\n‚Ä¢ Configurar notificaciones por email cuando recibas nuevas respuestas\n‚Ä¢ Analizar tendencias y patrones en los datos recibidos\n\nTodas las respuestas est√°n protegidas y solo t√∫ puedes acceder a ellas.';
        } else {
          botResponse = 'Gracias por tu pregunta. Puedo ayudarte con la personalizaci√≥n de formularios, tipos de campos disponibles, c√≥mo integrar formularios en tu sitio web, y c√≥mo administrar las respuestas. Si tienes preguntas espec√≠ficas sobre alguno de estos temas, no dudes en preguntar.';
        }

        setMessages(prev => [...prev, { content: botResponse, role: 'assistant' }]);
        setIsLoading(false);
        scrollToBottom();
      }, 1000);

    } catch (error) {
      console.error('Error al enviar mensaje:', error);
      toast({
        title: 'Error al enviar mensaje',
        description: 'Hubo un problema al comunicarse con el asistente. Por favor, intenta de nuevo.',
        variant: 'destructive',
      });
      setIsLoading(false);
    }
  };

  // Funci√≥n para desplazarse al √∫ltimo mensaje
  const scrollToBottom = () => {
    setTimeout(() => {
      chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, 100);
  };

  return (
    <div className="min-h-screen flex flex-col">
