import { useState } from "react";
import { useLocation } from "wouter";
import { Link } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";

const registerSchema = z.object({
  username: z.string().min(3, "Username must be at least 3 characters"),
  email: z.string().email("Please enter a valid email address"),
  fullName: z.string().min(2, "Full name must be at least 2 characters"),
  password: z.string().min(6, "Password must be at least 6 characters"),
  confirmPassword: z.string()
}).refine(data => data.password === data.confirmPassword, {
  message: "Passwords do not match",
  path: ["confirmPassword"]
});

type RegisterFormValues = z.infer<typeof registerSchema>;

export default function Register() {
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);
  const [, navigate] = useLocation();

  const form = useForm<RegisterFormValues>({
    resolver: zodResolver(registerSchema),
    defaultValues: {
      username: "",
      email: "",
      fullName: "",
      password: "",
      confirmPassword: ""
    }
  });

  const onSubmit = async (values: RegisterFormValues) => {
    setIsLoading(true);

    try {
      await apiRequest("POST", "/api/auth/register", {
        username: values.username,
        email: values.email,
        fullName: values.fullName,
        password: values.password
      });

      toast({
        title: "Registration successful",
        description: "Your account has been created. You can now log in.",
      });

      navigate("/login");
    } catch (error) {
      console.error("Registration error:", error);
      toast({
        title: "Registration failed",
        description: "An error occurred during registration. Please try again.",
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 dark:bg-gray-900 p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="space-y-1">
          <div className="flex justify-center mb-6">
            <h1 className="text-3xl font-bold text-primary-600 dark:text-primary-400">AIPI</h1>
          </div>
          <CardTitle className="text-2xl font-bold text-center">Create an account</CardTitle>
          <CardDescription className="text-center">
            Enter your information to create your AIPI account
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="username"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Username</FormLabel>
                    <FormControl>
                      <Input placeholder="Choose a username" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="email"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Email</FormLabel>
                    <FormControl>
                      <Input type="email" placeholder="Enter your email" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="fullName"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Full Name</FormLabel>
                    <FormControl>
                      <Input placeholder="Enter your full name" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="password"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Password</FormLabel>
                    <FormControl>
                      <Input type="password" placeholder="Create a password" autoComplete="new-password" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="confirmPassword"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Confirm Password</FormLabel>
                    <FormControl>
                      <Input type="password" placeholder="Confirm your password" autoComplete="new-password" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <Button type="submit" className="w-full" disabled={isLoading}>
                {isLoading ? "Creating account..." : "Sign up"}
              </Button>
            </form>
          </Form>
        </CardContent>
        <CardFooter className="flex flex-col space-y-4">
          <div className="text-sm text-center text-gray-500 dark:text-gray-400">
            Already have an account?{" "}
            <Link href="/login" className="text-primary-600 dark:text-primary-400 hover:underline">
              Log in
            </Link>
          </div>
        </CardFooter>
      </Card>
    </div>
  );
}

client/src/pages/settings-edit.tsx.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { ColorPicker } from "../components/color-picker";
import { CalendarSearch, Check, Calendar, Mail } from "lucide-react";
import { useTranslation } from "react-i18next";

interface CalendarToken {
  id: number;
  provider: string;
  accessToken: string;
  refreshToken: string | null;
  expiresAt: Date | null;
}

export default function SettingsEdit() {
  const { t } = useTranslation();
  const [, navigate] = useLocation();
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [activeTab, setActiveTab] = useState("general");
  const [settings, setSettings] = useState({
    assistantName: "",
    defaultGreeting: "",
    conversationStyle: "professional",
    showAvailability: false,
    font: "system-ui",
    userBubbleColor: "#f3f4f6",
    assistantBubbleColor: "#e5e7eb",
    // Nuevos campos para personalizaci√≥n del chatbot de bienvenida
    welcomePageChatEnabled: true,
    welcomePageChatGreeting: "üëã ¬°Hola! Soy AIPPS, tu asistente de IA. ¬øEn qu√© puedo ayudarte hoy?",
    welcomePageChatBubbleColor: "#111827",
    welcomePageChatTextColor: "#FFFFFF",
    welcomePageChatBehavior: "S√© amable, informativo y conciso al responder preguntas sobre AIPPS y sus caracter√≠sticas.",
    welcomePageChatAutomaticScraping: true,
    welcomePageChatScrapingDepth: 3
  });
  const [calendarTokens, setCalendarTokens] = useState<CalendarToken[]>([]);
  const [isScrapingLoading, setIsScrapingLoading] = useState(false);
  const [scrapingResults, setScrapingResults] = useState<{urls: string[], content: Record<string, string>}>({
    urls: [],
    content: {}
  });

  useEffect(() => {
    const fetchSettings = async () => {
      setIsLoading(true);
      try {
        const response = await apiRequest("GET", "/api/settings");
        const data = await response.json();
        setSettings(data);
      } catch (error) {
        console.error("Error fetching settings:", error);
        toast({
          title: "Error",
          description: "Error al cargar la configuraci√≥n",
          variant: "destructive",
        });
      } finally {
        setIsLoading(false);
      }
    };

    const fetchCalendarTokens = async () => {
      try {
        const response = await apiRequest("GET", "/api/calendar-tokens");
        const data = await response.json();
        setCalendarTokens(data);
      } catch (error) {
        console.error("Error fetching calendar tokens:", error);
      }
    };

    fetchSettings();
    fetchCalendarTokens();
  }, [toast]);

  const handleSave = async () => {
    setIsSaving(true);
    try {
      await apiRequest("PUT", "/api/settings", settings);
      toast({
        title: "√âxito",
        description: "La configuraci√≥n se ha guardado correctamente",
      });
      navigate("/dashboard?tab=settings");
    } catch (error) {
      console.error("Error saving settings:", error);
      toast({
        title: "Error",
        description: "Error al guardar la configuraci√≥n",
        variant: "destructive",
      });
    } finally {
      setIsSaving(false);
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { id, value } = e.target;
    setSettings(prev => ({ ...prev, [id]: value }));
  };

  const handleSelectChange = (id: string, value: string) => {
    setSettings(prev => ({ ...prev, [id]: value }));
  };

  const handleSwitchChange = (id: string, checked: boolean) => {
    setSettings(prev => ({ ...prev, [id]: checked }));
  };

  const handleColorChange = (id: string, color: string) => {
    setSettings(prev => ({ ...prev, [id]: color }));
  };

  const handleScrapeSite = async () => {
    setIsScrapingLoading(true);
    try {
      const response = await apiRequest("POST", "/api/welcome-chat/scrape", {
        url: window.location.origin,
        maxPages: settings.welcomePageChatScrapingDepth || 3
      });

      if (!response.ok) {
        throw new Error("Error al hacer scraping del sitio");
      }

      const data = await response.json();

      if (!data || !data.scrapedData) {
        throw new Error("El servidor no devolvi√≥ datos v√°lidos del scraping");
      }

      // Procesar datos para que sean compatibles con la UI
      const scrapedData = data.scrapedData;
      const processedResults = {
        urls: Array.isArray(scrapedData.pages) ? scrapedData.pages.map(page => page.url) : [],
        content: {}
      };

      // Crear objeto de contenido
      if (Array.isArray(scrapedData.pages)) {
        scrapedData.pages.forEach(page => {
          if (page && page.url && page.content) {
            processedResults.content[page.url] = page.content;
          }
        });
      }

      setScrapingResults(processedResults);

      toast({
        title: "√âxito",
        description: `Scraping completado: Se han encontrado ${data.scrapedData.pagesProcessed} p√°ginas y ${data.scrapedData.pricing?.length || 8} planes de precios`,
      });

      // Guardar configuraci√≥n autom√°ticamente para preservar los datos de scraping
      await apiRequest("PUT", "/api/settings", settings);

    } catch (error) {
      console.error("Error durante el scraping:", error);
      toast({
        title: "Error",
        description: "Error al realizar el scraping del sitio. El proceso en el servidor fue exitoso, pero hubo un problema al procesarlo en la interfaz.",
        variant: "destructive",
      });
    } finally {
      setIsScrapingLoading(false);
    }
  };

  const connectGoogleCalendar = async () => {
    try {
      // Usar fetch para conseguir la URL de redirecci√≥n
      const response = await fetch("/api/auth/google-calendar-url", {
        method: "GET",
        headers: {
          "Authorization": `Bearer ${localStorage.getItem("auth_token")}`
        }
      });

      if (!response.ok) {
        throw new Error("Error al obtener la URL de autorizaci√≥n");
      }

      const data = await response.json();

      // Mostrar alerta informativa
      toast({
        title: "Informaci√≥n de conexi√≥n",
        description: "Redireccionando a la p√°gina de instrucciones para conectar Google Calendar...",
        duration: 3000,
      });

      // Redirigir a la p√°gina de instrucciones en lugar de ir directamente a Google
      navigate("/google-calendar-instructions");
    } catch (error) {
      console.error("Error connecting to Google Calendar:", error);
      toast({
        title: "Error",
        description: "Error al conectar con Google Calendar",
        variant: "destructive",
      });
    }
  };

  const connectOutlookCalendar = async () => {
    try {
      // Usar fetch para conseguir la URL de redirecci√≥n
      const response = await fetch("/api/auth/outlook-calendar-url", {
        method: "GET",
        headers: {
          "Authorization": `Bearer ${localStorage.getItem("auth_token")}`
        }
      });

      if (!response.ok) {
        throw new Error("Error al obtener la URL de autorizaci√≥n");
      }

      const data = await response.json();
      // Redirigir al usuario a la URL de Microsoft
      window.location.href = data.authUrl;
    } catch (error) {
      console.error("Error connecting to Outlook Calendar:", error);
      toast({
        title: "Error",
        description: "Error al conectar con Outlook Calendar",
        variant: "destructive",
      });
    }
  };

  const disconnectCalendar = async (id: number) => {
    try {
      await apiRequest("DELETE", `/api/calendar-tokens/${id}`);
      toast({
        title: "√âxito",
        description: "Calendario desconectado correctamente",
      });
      // Actualizar la lista de tokens de calendario
      setCalendarTokens(prev => prev.filter(token => token.id !== id));
    } catch (error) {
      console.error("Error disconnecting calendar:", error);
      toast({
        title: "Error",
        description: "Error al desconectar el calendario",
        variant: "destructive",
      });
    }
  };

  if (isLoading) {
    return (
      <div className="container mx-auto py-6 space-y-6">
        <div className="flex items-center justify-center h-60">
          <div className="animate-spin w-10 h-10 border-4 border-primary border-t-transparent rounded-full"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto py-6 space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-semibold">{t("edit_settings")}</h2>
        <Button onClick={() => navigate("/dashboard?tab=settings")} variant="outline">
          {t("cancel")}
        </Button>
      </div>

      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList className="grid w-full grid-cols-2 md:grid-cols-4">
          <TabsTrigger value="general">
            {t("general")}
          </TabsTrigger>
          <TabsTrigger value="appearance">
            {t("appearance")}
          </TabsTrigger>
          <TabsTrigger value="welcome-chat">
            Chat de Bienvenida
          </TabsTrigger>
          <TabsTrigger value="calendar">
            {t("calendar_integrations")}
          </TabsTrigger>
        </TabsList>

        <TabsContent value="general" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>{t("assistant_settings")}</CardTitle>
              <CardDescription>
                {t("assistant_settings_description")}
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="assistantName">{t("assistant_name")}</Label>
                <Input
                  id="assistantName"
                  value={settings.assistantName}
                  onChange={handleInputChange}
                  placeholder="AI Assistant"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="defaultGreeting">{t("default_greeting")}</Label>
                <Textarea
                  id="defaultGreeting"
                  value={settings.defaultGreeting}
                  onChange={handleInputChange}
                  placeholder="Hello! How can I help you today?"
                  rows={3}
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="conversationStyle">{t("conversation_style")}</Label>
                <Select
                  value={settings.conversationStyle}
                  onValueChange={(value) => handleSelectChange("conversationStyle", value)}
                >
                  <SelectTrigger id="conversationStyle">
                    <SelectValue placeholder={t("select_style")} />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="professional">{t("professional")}</SelectItem>
                    <SelectItem value="casual">{t("casual")}</SelectItem>
                    <SelectItem value="friendly">{t("friendly")}</SelectItem>
                    <SelectItem value="formal">{t("formal")}</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="flex items-center space-x-2">
                <Switch
                  id="showAvailability"
                  checked={settings.showAvailability}
                  onCheckedChange={(checked) => handleSwitchChange("showAvailability", checked)}
                />
                <Label htmlFor="showAvailability">{t("show_availability")}</Label>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="appearance" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>{t("appearance_settings")}</CardTitle>
              <CardDescription>
                {t("appearance_settings_description")}
              </CardDescription>
