/**
 * Verifica si un usuario puede realizar una acci√≥n dentro de su presupuesto
 * CORREGIDO: Usa operaciones de DB con NUMERIC para evitar problemas de precisi√≥n
 */
export async function checkBudgetAvailability(
  userId: number, 
  actionType: ActionType
): Promise<BudgetCheckResult> {
  try {
    // Usar SELECT FOR UPDATE para obtener presupuesto de forma at√≥mica
    const result = await db.transaction(async (tx) => {
      // 1. Obtener presupuesto con lock at√≥mico
      const userBudgetQuery = await tx
        .select()
        .from(userBudgets)
        .where(eq(userBudgets.userId, userId))
        .for('update')
        .limit(1);

      if (userBudgetQuery.length === 0) {
        return {
          userBudget: null,
          actionCost: null,
          reason: "Usuario no tiene presupuesto configurado. Contacte al administrador."
        };
      }

      const userBudget = userBudgetQuery[0];

      // 2. Verificar si est√° suspendido
      if (userBudget.isSuspended) {
        return {
          userBudget,
          actionCost: null,
          reason: "Presupuesto mensual agotado. Aumente su presupuesto para continuar."
        };
      }

      // 3. Obtener costo de la acci√≥n
      const actionCostQuery = await tx
        .select()
        .from(actionCosts)
        .where(and(
          eq(actionCosts.actionType, actionType),
          eq(actionCosts.isActive, true)
        ))
        .limit(1);

      if (actionCostQuery.length === 0) {
        return {
          userBudget,
          actionCost: null,
          reason: `Costo no configurado para acci√≥n: ${actionType}`
        };
      }

      const actionCost = actionCostQuery[0];

      // 4. Verificar fondos usando comparaci√≥n directa en DB
      const budgetAnalysis = await tx
        .select({
          monthlyBudget: userBudgets.monthlyBudget,
          currentSpent: userBudgets.currentSpent,
          remainingBudget: userBudgets.monthlyBudget,
          hasEnoughFunds: sql<boolean>`(monthly_budget - current_spent) >= ${actionCost.finalCost}`
        })
        .from(userBudgets)
        .where(eq(userBudgets.userId, userId))
        .limit(1);

      if (budgetAnalysis.length === 0) {
        return {
          userBudget,
          actionCost,
          reason: "Error al calcular presupuesto"
        };
      }

      // Calcular porcentajes en JavaScript para simplificar tipos
      const monthlyBudget = parseFloat(budgetAnalysis[0].monthlyBudget);
      const currentSpent = parseFloat(budgetAnalysis[0].currentSpent);
      const actionCostValue = parseFloat(actionCost.finalCost);
      const remainingBudget = monthlyBudget - currentSpent;
      const percentageUsed = (currentSpent / monthlyBudget) * 100;
      const newPercentage = ((currentSpent + actionCostValue) / monthlyBudget) * 100;

      return {
        userBudget,
        actionCost,
        analysis: {
          ...budgetAnalysis[0],
          remainingBudget: remainingBudget.toFixed(2),
          percentageUsed,
          newPercentage,
          hasEnoughFunds: budgetAnalysis[0].hasEnoughFunds
        }
      };
    });

    // Si hay error en la transacci√≥n
    if (result.reason) {
      return {
        allowed: false,
        userBudget: result.userBudget,
        actionCost: result.actionCost,
        costToApply: "0.00",
        remainingBudget: "0.00",
        percentageUsed: 0,
        reason: result.reason
      };
    }

    const { userBudget, actionCost, analysis } = result;

    if (!userBudget || !actionCost || !analysis) {
      return {
        allowed: false,
        userBudget,
        actionCost,
        costToApply: "0.00",
        remainingBudget: "0.00",
        percentageUsed: 0,
        reason: "Error interno al procesar datos"
      };
    }

    // Verificar si hay fondos suficientes
    if (!analysis.hasEnoughFunds) {
      return {
        allowed: false,
        userBudget,
        actionCost,
        costToApply: actionCost.finalCost,
        remainingBudget: analysis.remainingBudget,
        percentageUsed: Math.min(analysis.percentageUsed, 100),
        reason: `Fondos insuficientes. Costo: $${actionCost.finalCost} ${userBudget.currency}, Disponible: $${analysis.remainingBudget} ${userBudget.currency}`
      };
    }

    // Determinar si se activar√° una alerta
    let willTriggerAlert = undefined;
    if (analysis.percentageUsed < 50 && analysis.newPercentage >= 50 && userBudget.alertThreshold50) {
      willTriggerAlert = { threshold: 50, type: 'threshold_50' as const };
    } else if (analysis.percentageUsed < 80 && analysis.newPercentage >= 80 && userBudget.alertThreshold80) {
      willTriggerAlert = { threshold: 80, type: 'threshold_80' as const };
    } else if (analysis.percentageUsed < 90 && analysis.newPercentage >= 90 && userBudget.alertThreshold90) {
      willTriggerAlert = { threshold: 90, type: 'threshold_90' as const };
    } else if (analysis.newPercentage >= 100 && userBudget.alertThreshold100) {
      willTriggerAlert = { threshold: 100, type: 'budget_exceeded' as const };
    }

    return {
      allowed: true,
      userBudget,
      actionCost,
      costToApply: actionCost.finalCost,
      remainingBudget: (parseFloat(analysis.remainingBudget) - parseFloat(actionCost.finalCost)).toFixed(2),
      percentageUsed: Math.min(analysis.newPercentage, 100),
      willTriggerAlert
    };

  } catch (error) {
    console.error(`Error al verificar presupuesto para usuario ${userId}:`, error);
    return {
      allowed: false,
      userBudget: null,
      actionCost: null,
      costToApply: "0.00",
      remainingBudget: "0.00",
      percentageUsed: 0,
      reason: "Error interno al verificar presupuesto"
    };
  }
}

/**
 * Cobra una acci√≥n al presupuesto del usuario y registra el uso
 * CORREGIDO: Usa operaciones at√≥micas con SELECT FOR UPDATE y NUMERIC precision
 */
export async function chargeUserBudget(
  userId: number,
  actionType: ActionType,
  resourceId?: number,
  resourceType?: string,
  metadata?: any
): Promise<ChargeResult> {
  try {
    // Generar billing month (YYYY-MM)
    const now = new Date();
    const billingMonth = `${now.getFullYear()}-${String(now.getMonth() + 1).padStart(2, '0')}`;

    // Transacci√≥n at√≥mica para evitar race conditions
    const result = await db.transaction(async (tx) => {
      // 1. SELECT FOR UPDATE para lock at√≥mico del presupuesto
      const userBudgetQuery = await tx
        .select()
        .from(userBudgets)
        .where(eq(userBudgets.userId, userId))
        .for('update')
        .limit(1);

      if (userBudgetQuery.length === 0) {
        throw new Error("Usuario no tiene presupuesto configurado");
      }

      const userBudget = userBudgetQuery[0];

      // 2. Verificar si est√° suspendido
      if (userBudget.isSuspended) {
        throw new Error("Presupuesto mensual agotado");
      }

      // 3. Obtener costo de la acci√≥n
      const actionCostQuery = await tx
        .select()
        .from(actionCosts)
        .where(and(
          eq(actionCosts.actionType, actionType),
          eq(actionCosts.isActive, true)
        ))
        .limit(1);

      if (actionCostQuery.length === 0) {
        throw new Error(`Costo no configurado para acci√≥n: ${actionType}`);
      }

      const actionCost = actionCostQuery[0];

      // 4. Verificar fondos suficientes primero
      const currentBudgetSpent = parseFloat(userBudget.currentSpent);
      const monthlyBudgetAmount = parseFloat(userBudget.monthlyBudget);
      const costToApply = parseFloat(actionCost.finalCost);
      const remainingBudget = monthlyBudgetAmount - currentBudgetSpent;

      if (costToApply > remainingBudget) {
        throw new Error("Fondos insuficientes para realizar la acci√≥n");
      }

      const newSpent = currentBudgetSpent + costToApply;
      const budgetExceeded = newSpent >= monthlyBudgetAmount;

      // 5. Actualizar presupuesto at√≥micamente
      const updateResult = await tx
        .update(userBudgets)
        .set({
          currentSpent: newSpent.toString(),
          isSuspended: budgetExceeded,
          suspendedAt: budgetExceeded ? new Date() : undefined,
          updatedAt: new Date()
        })
        .where(eq(userBudgets.userId, userId))
        .returning({
          id: userBudgets.id,
          newCurrentSpent: userBudgets.currentSpent,
          monthlyBudget: userBudgets.monthlyBudget,
          isSuspended: userBudgets.isSuspended,
          currency: userBudgets.currency
        });

      if (updateResult.length === 0) {
        throw new Error("Error al actualizar presupuesto");
      }

      const updatedBudget = updateResult[0];

      // 6. Registrar uso con actionCostId para auditor√≠a
      const usageRecord = {
        userId,
        actionType,
        actionCostId: actionCost.id, // NUEVO: Versionado de precios
        costApplied: actionCost.finalCost,
        currency: userBudget.currency,
        resourceId,
        resourceType,
        billingMonth,
        metadata
      };

      const insertedUsage = await tx.insert(usageTracking).values([usageRecord]).returning();

      // 7. Calcular porcentajes para alertas
      const currentPercentage = (newSpent / monthlyBudgetAmount) * 100;
      const previousPercentage = (currentBudgetSpent / monthlyBudgetAmount) * 100;

      const percentages = {
        currentPercentage,
        previousPercentage
      };

      return {
        transactionId: insertedUsage[0]?.id,
        updatedBudget,
        actionCost,
        percentages,
        userBudget
      };
    });

    // 8. Procesar alerta FUERA de la transacci√≥n para evitar bloqueos largos
    let alertTriggered = undefined;
    if (result.percentages && result.userBudget) {
      alertTriggered = await processAlertIfNeeded(
        userId,
        result.userBudget,
        result.percentages.previousPercentage,
        result.percentages.currentPercentage,
        billingMonth
      );
    }

    return {
      success: true,
      transactionId: result.transactionId,
      newCurrentSpent: result.updatedBudget.newCurrentSpent,
      budgetExceeded: result.updatedBudget.isSuspended || false,
      alertTriggered
    };

  } catch (error) {
    console.error(`Error al cobrar presupuesto para usuario ${userId}:`, error);
    return {
      success: false,
      error: error instanceof Error ? error.message : "Error interno al procesar el cobro"
    };
  }
}

/**
 * Procesa y persiste alertas de presupuesto cuando se cruzan umbrales
 * NUEVO: Persistir alertas en sent_alerts table en lugar de solo console.log
 */
async function processAlertIfNeeded(
  userId: number,
  userBudget: UserBudget,
  previousPercentage: number,
  currentPercentage: number,
  billingMonth: string
): Promise<{ threshold: number; type: string; alertId?: number } | undefined> {
  try {
    let alertToSend = undefined;

    // Determinar qu√© alerta enviar basado en umbrales cruzados
    if (previousPercentage < 50 && currentPercentage >= 50 && userBudget.alertThreshold50) {
      alertToSend = { threshold: 50, type: 'threshold_50' };
    } else if (previousPercentage < 80 && currentPercentage >= 80 && userBudget.alertThreshold80) {
      alertToSend = { threshold: 80, type: 'threshold_80' };
    } else if (previousPercentage < 90 && currentPercentage >= 90 && userBudget.alertThreshold90) {
      alertToSend = { threshold: 90, type: 'threshold_90' };
    } else if (currentPercentage >= 100 && userBudget.alertThreshold100) {
      alertToSend = { threshold: 100, type: 'budget_exceeded' };
    }

    if (!alertToSend) {
      return undefined;
    }

    // Persistir alerta en la base de datos
    const alertRecord = {
      userId,
      alertType: alertToSend.type,
      thresholdReached: alertToSend.threshold,
      currentSpent: userBudget.currentSpent,
      monthlyBudget: userBudget.monthlyBudget,
      deliveryMethod: 'email', // Por defecto email, se puede expandir
      deliveryStatus: 'pending',
      emailAddress: null, // Se llenar√° cuando se implemente el env√≠o
      messageContent: `Alerta de presupuesto: Has alcanzado el ${alertToSend.threshold}% de tu presupuesto mensual.`,
      billingMonth
    };

    try {
      const insertedAlert = await db
        .insert(sentAlerts)
        .values([alertRecord])
        .onConflictDoNothing() // Evitar duplicados por el constraint √∫nico
        .returning({ id: sentAlerts.id });

      const alertId = insertedAlert[0]?.id;

      console.log(`üö® Alerta persistida: ${alertToSend.type} para usuario ${userId} - ${currentPercentage.toFixed(1)}% del presupuesto`);

      return {
        ...alertToSend,
        alertId
      };
    } catch (alertError) {
      // Si falla por constraint √∫nico (alerta duplicada), simplemente logear
      console.log(`‚ÑπÔ∏è Alerta ${alertToSend.type} ya enviada para usuario ${userId} en ${billingMonth}`);
      return alertToSend;
    }

  } catch (error) {
    console.error(`Error al procesar alerta para usuario ${userId}:`, error);
    return undefined;
  }
}

